css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geocaching.com") {

/*Adds borders between rows of all tables */
tr {
border-top: 1px solid #d7d7d7 ! important;
/* border-bottom: 1px solid #d7d7d7 ! important; */
}

/* Reduces table padding */
table th,table td{
padding:.1em .3em .1em .3em ! important;
}


/* Darkens alternating rows of table on profile page and a few other tables. */
table.Table tr.AlternatingRow td,.AlternatingRow,table.Table tr td.AlternatingRow,tr.Row1 td,tr.Row3 
td,tr.Row5 td,tr.Row7 td,tr.Row9 td{
background-color:#eff4f9 ! important;
}

/* Reduces line spacing of widgits (right side bar items.) */
.WidgetBody { line-height: 110% ! important; }


/* Reduces line spacing on My Friends page. */
.FriendWidget { line-height: 110% ! important; }
/* Adds border between friend name and friend details on My Friends page. */
.FriendList { border-top:1px solid #a3a3a3; }
}

@-moz-document url-prefix("http://www.geocaching.com/seek/cache_details") {
/*Reduces line spacing in text of logs. */
table.Table {line-height: 1.5em ! important }
/*Reduces line spacing of cache text. */
.UserSuppliedContent {line-height: 1.5em ! important }
}

@-moz-document url-prefix(http://www.geocaching.com/seek/nearest*) {
/*Reduces line spacing of cache search results. */
table.Table {line-height: 100% ! important }
}

@-moz-document url-prefix(http://www.geocaching.com/my*) {
/*Reduces line spacing of profile page. */
table.Table {line-height: 100% ! important }
}
