css:
/* Bash.Org.Ru Prettifier */
/* By almlex ( http://almlex.net/, http://twitter.com/almlex/ ) */
/* v0.7.1 Beta */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {

/* Random Stuff */
body {background:#fff !important;color:#000 !important}
a {color:#06f !important;}
a:hover {color:#03f !important;}
#navstrip {display:none}

h2, h3 {
 font-family:Georgia !important;
 color:#000 !important;
}

#page {width:850px !important}
a {text-decoration:underline !important;}

.q form[action="/abyssbest"] input[type="submit"] {position:relative;top:-6px;}

/* Header */
.header {height:45px!important;text-align:left!important;font-size:250% !important;color:#000 !important;overflow:hidden;}
.header img {display:none}
.header a:before {content:'bash.org.ru â€” Ð¦Ð¸Ñ‚Ð°Ñ‚Ð½Ð¸Ðº Ð ÑƒÐ½ÐµÑ‚Ð°'}
.header a {color:#000!important;text-decoration:none!important;display:block;margin-bottom:100px;}
.header p {margin:0px !important}
#bar {display:none}

.menu {
 padding:8px;
 margin:15px 0 10px!important;
}

.menu {position:relative;z-index:15;}

.menu > div:first-child {width:545px !important;}

.menu a {
 background:#f0f1e7;
 padding:3px 5px;
 margin:0 -5px 0 -4px;
 line-height:2.3;
 color:#000 !important;
 position:relative;
 z-index:15;
 border:5px solid #FFF;
 border-width:0 5px;
}
.menu a:hover {background:#eaebd5;}

.menu input[type="text"] {width:220px !important}
.menu input[type="submit"] {margin-top:-1px !important}

.news.stats.b { background:#fff!important;border:0!important;margin-top:5px!important; }

/* Quotes */

#quotes br:first-child {margin-top:5px !important;}

.pager a {
 background:#f0f1e7 !important;
 padding:1px 10px !important;
 margin:0 5px !important;
 color:#000 !important;
}
.pager a:hover {background:#eaebd5!important;}

#quotes > div[style="font-size: smaller;"] a { color:#999 !important; }

#quotes .q > div:last-child {
 border:0 !important;
 border-bottom:1px solid #CCC !important;
 background:0 !important;
 font-family:Verdana;
 line-height:1.5 !important;
}

#quotes .q .vote {line-height:1.6;}
#quotes .q .vote a {color:#000 !important}
#quotes .q .vote a:first-child, #quotes .q .vote b:first-child {
 font-size:160%;
 font-family:Georgia;
 text-decoration:none !important;
 margin-right:100%;
 font-weight:normal;
}
#quotes .q .vote a:first-child:before, #quotes .q .vote b:first-child:before {content:'#'}

/* Comics */

td.navi a {
 background:#f0f1e7;
 padding:5px 10px !important;
 color:#000 !important;
}

td.navi a:hover {background:#eaebd5 !important;}

/* end @-moz-document */ }
