css:
@-moz-document domain("lostfilm.tv") {
html, body {
  margin: 0px !important;
  padding: 0px !important;
  background: url(data:image/png;base64,) repeat-x scroll 0 0 !important;
  background-color: #90A990 !important;
  color: #000000 !important;
}
body>div:first-child {
  margin: 0px auto !important;
  width: 94% !important;
  min-width: 892px !important; 
}
#MainDiv {
  margin: 0px !important;
  width: 100% !important;
  min-width: 892px !important; 
}
.left {
  width: 20.5% !important;
}
#MainDiv>div:first-child>div:nth-last-child(2){
  width: 56% !important;
  min-width: 500px !important;
}
#Onwrapper {
  width: 94% !important;
  min-width: 892px !important; 
}
.right {
  width: 20.5% !important;
}
a {
  color: #000000 !important;
}
img {
  border: 0 none !important;
}
nobr {
  white-space: normal;
}
.img_border {
  border: 0 none !important;
}
.user_avatar {
    opacity: 1 !important;
}
.up {
  background-color: transparent !important; 
}
.up:hover{
  background-color: #D00A0E !important; 
  color: #FFFFFF !important;
}
.t_episode_title {
  color: #000000 !important;
}
.block {
  background-repeat: repeat-x !important;
}
.block_link a {
  color: #FFFFFF !important;
}
.menu a {
  color: #FFFFFF !important;
  margin-top: 3px !important;
}
.user_avatar img {
  opacity: 0.9 !important;
}
.prof {
  color: #000000 !important;
}
.wh {
  color: #000000 !important;
}
.content_body {
  color: #000000 !important;
}
.logo {
  height: 25px !important;
  color: #000000 !important;
}
.main_logo_link, .disclaimer, .bb_nopadding, #ta_link, #myA, .right object, div[class="right"]>a, div[class="left"]>div:nth-last-child(-n+10), #Onwrapper>div:nth-child(3), iframe, #bogi_panel, .content_body table tbody tr:first-child, #onlime, body>div:nth-child(2) {
  display: none !important; 
}
.prof {
  display: block !important; 
}
.content_head {
  height: auto !important; 
  position: inherit !important;
}
}

@-moz-document url-prefix("http://lostfilm.tv/phpbb2.php"), url-prefix("http://www.lostfilm.tv/phpbb2.php") {
.left, .right, .block_bottoml, .block_bottomc, .block_bottomr {
  display: none !important; 
}
body div div {
  margin: 0% !important;
}
body>div:first-child {
  margin: 0px auto;
  width: 90% !important;
  min-width: 450px !important; 
}
body>div:first-child>div:nth-last-child(2){
  width: 100% !important;
}
.f_row1, .f_row2 {
  text-align: left !important;
}
.hidden_images {
  max-width: 800px !important;
  cursor: pointer  !important;
}
.thTop2, .thTop2>span>a {
  color: #FFFFFF !important;
}
iframe {
  display: block !important; 
}
body>div:first-child>div:nth-child(3){
  display: none !important; 
}
}

@-moz-document domain("lostfilm.info") {
#bogi_panel {
  display: none !important; 
}
}
