css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://myanimelist.net/), 
url-prefix(http://myanimelist.net/ab),
url-prefix(http://myanimelist.net/ad),
url-prefix(http://myanimelist.net/anime.),
url-prefix(http://myanimelist.net/anime/),
url-prefix(http://myanimelist.net/b),
url-prefix(http://myanimelist.net/c),
url-prefix(http://myanimelist.net/d),
url-prefix(http://myanimelist.net/e),
url-prefix(http://myanimelist.net/f),
url-prefix(http://myanimelist.net/h),
url-prefix(http://myanimelist.net/i),
url-prefix(http://myanimelist.net/l),
url-prefix(http://myanimelist.net/manga.),
url-prefix(http://myanimelist.net/manga/),
url-prefix(http://myanimelist.net/mo),
url-prefix(http://myanimelist.net/my),
url-prefix(http://myanimelist.net/n),
url-prefix(http://myanimelist.net/p),
url-prefix(http://myanimelist.net/r),
url-prefix(http://myanimelist.net/s),
url-prefix(http://myanimelist.net/t),
url-prefix(http://myanimelist.net/u) {

	* {
			font-family: "Arial", "Meiryo", "Malgun Gothic" !important;
		}

	body {
		margin-top: 1px !important;
		font-size: 75% !important;
		background:url("http://thayanger.neostrada.pl/malstyle/bg.jpg") no-repeat scroll center top !important;
	}

	#searchBar {
		padding-top: 7px !important;
	}

	#searchBar #topSearchText.inputtext {
		border: #fff 1px solid !important;
	}

	#searchBar #topSearchValue.inputtext {
		border: #fff 1px solid !important ;
	}

	.inputtext, .textarea {
		font-family: tahoma, sans-serif !important;
	}

	#horiznav_nav ul li a {
		-moz-border-radius-topleft: 3px !important;
		-moz-border-radius-topright: 3px !important;
		font-size: 11px !important;
	}

	#horiznav_nav ul li a:hover {
		background-color: #B0C4E8 !important;
	}

	#horiznav_nav ul li a.horiznav_active:hover {
		background-color: #1D439B !important;
	}

	.horiznav_active {
		font-weight: bold !important;
	}

	#panel_left br:first-child   {
		margin-bottom: -5px !important;
	}

	td {
		font-size: 1em !important;
	}

	.normal_header, .normal_header strong, h2 {
		font-family: "Arial Narrow", "Corbel", "Arial" !important;
		text-transform: uppercase !important;
		letter-spacing: 1px !important;
		color: #333 !important;
	}

	h1{
		font-family: "Corbel", "Arial" !important;
		font-weight: bold !important;
		font-size: 17px !important
	}

	/*Anime Blue Titles */
	div[id*='myarea']+a {
		font-family: "Corbel", "Arial" !important;
		font-weight: bold !important;
	}

	/*Profile comment user name*/
	.dark_text a {
		font-family: "Corbel", "Arial" !important;
		font-size: 14px !important;
		font-weight: bold !important;
		letter-spacing: -0.25px !important;
	}

	/*Forum */
	span.forum_locheader {
		font-family: "Arial Narrow", "Corbel" !important;
		font-size: 1.4em !important;
	}

	td.forum_category[colspan="2"] {
		-moz-border-radius-topleft: 3px !important;
		-moz-border-radius-topright: 3px !important;
	}

	/*Orginal width (1060) + side menu (132)*/
	#myanimelist{
		width: 1192px !important;
	}

	/*Old header text*/
	#menu{
		background:url("http://thayanger.neostrada.pl/malstyle/header.png") no-repeat scroll left top;
		width: 1192px !important;
		-moz-border-radius-topleft: 5px !important;
		-moz-border-radius-topright: 5px !important;
		-moz-box-shadow: 0px 2px 6px #999 !important;
		background-color: #2E51A2 !important;
	}

	/*Content moved to right by side menu size (132)*/
	#contentWrapper {
		padding: 1px 0px 0px 132px !important;
	}

	/*Menu Position*/
	#menu_left{
		float: none !important;
		width: 0px !important;
		padding: 31px 0px 0px 0px !important;
	}

	/*Menu Header Style*/
	#menu_left #nav>li>a {
		height:17px !important;
		width:130px !important;
		padding: 3px 0px 1px 0px !important;
		/*Anty blink*/
		background:none repeat scroll 0 0 #2E51A2 !important;
		color: #FFFFFF !important;
		-moz-border-radius-topleft: 3px !important;
		-moz-border-radius-topright: 3px !important;
		font-family: "Arial Narrow", "Corbel" !important;
		font-size: 1.2em !important;
		-moz-box-shadow: 0px 2px 6px #999 !important;
	}

	#menu_left #nav>li {		
		margin: 5px 0px 0px 0px !important;
	}

	/*Menu Items Style List*/
	#menu_left #nav>li>ul {
		display: block !important;
		background-color: #fff !important;
		-moz-border-radius-bottomleft: 2px !important;
		-moz-border-radius-bottomright: 2px !important;
		-moz-box-shadow: 0px 2px 6px #999 !important;
		width:130px !important;
	}

	#menu_left #nav li ul li {
		background: none !important;
	}

	#menu_left #nav li ul li:hover {
		-moz-border-radius: 0; margin-top: 0 !important;
		margin-bottom: 0 !important;
		-moz-box-shadow: none;
	}

	#menu_left #nav li:hover {
		background-color: #E1E7F5 !important;
	}

	#menu_left #nav>li>ul>li a:hover {
		color: #FF0000 !important;
	}

	#menu_left #nav>li>ul>li a {
		background: none !important;
	}

	#menu_left #nav ul.wider span.edit a:hover {
		background-color: #fff !important;
		-moz-border-radius: 2px;
	}

	#menu_left #nav ul.wider span.edit a {
		height: 18px !important;
		line-height: 18px !important;
		margin: 1px 2px 0 0 !important;
		font-size: 10px !important;
	}

	/*Menu Positions*/
	#menu_left>#nav * {
		float:none !important;		
	}

	#menu_left>#nav li *  {
		position: relative !important;		
	}

	#menu_left>#nav>li .edit {
		position: absolute !important;
		left: 100px !important;	
		z-index: 1000 !important;
	}

	#menu_left>#nav>li .new {
		position: absolute !important;
		left: 65px !important;	
		z-index: 1000 !important;
	}

	/*Menu Items Style List Items*/
	#menu_left #nav li ul li {
		height:20px !important;
		width:130px !important;
	}

	/*Menu Items Style Links (Edit)*/
	#menu_left #nav>li>ul a {
		height: 17px !important;
		padding: 2px 2px 1px 0px !important;
	}
	
	/*Everyone knows what it is ^_^*/
	#header{
		display: none!important;
	}

	#headerSmall{
		display: none!important;
	}
}
