css:
@-moz-document domain(mail.google.com) {

*{font:Calibri !important;}
/*{font-size:8.0px !important;}
/*{font:Georgia !important;}
/*{font:Calibri !important;}
/*{font:Arial !important;}
/*{font:Helvetica !important;}

/* UNREAD rows - background colour and opacity and text */
 .zE {
    background-color: transparent !important;
    color: #FF3333 !important;
    font-size: 8px !important;
}
/* READ rows - background colour and opacity and text */
 .yO {
    background-color: transparent !important;
    color: white !important;
    font-size: 8px !important;
}
/* Hover Coloring - UNREAD */
 table.zt tr.zE:hover {
    background-color: #111111 !important;
    color: #FF0000 !important;
    font-size: 8px !important;
}
/* Hover Coloring - READ */
 table.zt tr.yO:hover {
    background-color: #111111 !important;
    color:#0088FF !important;
    font-size: 8px !important;
}
/* SELECTED rows - background colour and opacity*/
 .x7 {
    background-color: #000000 !important;
}
/* Hover Coloring - SELECTED */
 table.zt tr.x7:hover {
    background-color: #333333 !important;
}
/* Row dividing line colour and opacity */
 .xY {
    border-color: #888 !important;
}
/* Snippet Text Changes */
 .y2 {
    color: #FFFF33 !important;
    /*font-family:; font-style:;*/
}
/* === space adjustment */
 .xX {
    width:9em !important;
    padding-right:0;
}
/* for japanese */
 html:lang(ja) .xX, html[lang="ja"] .xX {
    width:9em !important;
}
/* for vertical split layout*/
 .apt {
    padding-right:0 !important;
}
.apm {
    margin-right:0 !important;
}


/* Gmail Date+Time in Mail List Ver.20180603 by nukumori.org */
/* # space adjustment */
/* ## define */
:root {
  --datetime-width: 16.5em;
}

/* ### ja */
:root:lang(ja) {
/*  --datetime-width: 9.25em; */
  --datetime-width: 13em;
}

/* ### Mac */
:root body[jscontroller="Gz34U"] {
  --datetime-width: 14em;
}

/* ## apply */
.xX {
  width: var(--datetime-width) !important;
  padding-right: 0;
}
.xX:lang(ja) {
  width: var(--datetime-width) !important;
}

/* # for vertical split layout*/
.apt {
  padding-right: 0 !important;
}
.apm {
  margin-right: 0 !important;
}

/* # show full date+time of title attribute */
.apm > span:after, /* for vertical split layout */
.xW > span:before {
  content: attr(title);
  color: #33FF33;
}
.apm > span, /* for vertical split layout */
.xW > span {
  color: transparent;
  overflow: hidden;
  float: left;
}
.apm > span > b, /* for vertical split layout */
.xW > span > b {
  display: none;
}

/* # sender width for vertical row layout */
/* ******** deleted 2012-01-21 ********
.yX.xY.apy{
  width:10% !important;
}
.yf.xY.apt{
  width:90% !important;
}
*/

/* ******** added 2018-06-03 ******** */

/* # bold unread */
.zE .apm,
.zE .xW {
  font-weight: 600;
}

/* # new look 2018 */
body[jscontroller] .Zs .zA > .WA + .yX,
body[jscontroller] .Zs .zA > .bnk + .yX {
/* original width: 100% - (header: 76px) - (icon + date/time: 110px) */
  max-width: calc(100% - 10px - var(--datetime-width) - 10px);
}

/* ******** date time ******** */
body[jscontroller] .apm, /* for vertical split layout */
body[jscontroller] .xW {
  overflow: hidden;
}
body[jscontroller] .apm > span, /* for vertical split layout */
body[jscontroller] .xW > span {
  overflow: visible;
  width: calc(var(--datetime-width) + 1px);
}

/* schedule and attachment icon */
body[jscontroller] .Zs .zA > .yf {
  margin-left: 0;
}

/* ## horizontal split datetime */
body[jscontroller] .zA > .xW {
  max-width: calc(var(--datetime-width) + 1px);
  flex-basis: auto;
}
}
