css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {

/* pageview graph */
div.funsizegraph span.bar {
float:right;
/*margin-right:2px;*/
width:5px;
height:44px;
border:0 solid;
border-right-width:2px;
_width:7;
overflow:hidden;
position:relative;
top:44px;
background:#b91080 url(http://i225.photobucket.com/albums/dd93/irem58127/CSS/graphpink2.png) repeat-x!important;

}

/* Search bars */
#searchForm {
background:transparent url('http://i225.photobucket.com/albums/dd93/irem58127/CSS/searchpink.png') repeat-x scroll 0 -30px !important ;
}

#searchForm div.form {
background:transparent url('http://i225.photobucket.com/albums/dd93/irem58127/CSS/searchpink.png') no-repeat scroll left top !important ;
}

#searchForm input.itext, #searchForm input.empty:focus {
background:transparent url('http://i225.photobucket.com/albums/dd93/irem58127/CSS/searchpink.png') repeat-x scroll 0 -30px !important ;
left: 24px !important ;
}

#searchForm input.empty {
background:transparent none repeat scroll 0 0 !important ;
}

#searchForm input.ibutton a {
background:transparent url('http://i225.photobucket.com/albums/dd93/irem58127/CSS/searchpink.png') repeat-x scroll 100% -60px !important ;
}

#searchForm ul.select li a {
background:transparent url('http://i225.photobucket.com/albums/dd93/irem58127/CSS/searchpink.png') repeat-x scroll 0 -60px !important ;
}

div.popup2-search a:hover, div popup2-search a:focus {
background:transparent url('http://e.deviantart.com/emoticons/b/bulletpink.gif') no-repeat 3px 4px !important ;
background-color: #ffb3e9 !important ;
color: #ffffff !important ;
padding-left: 23px !important ;
}

div.popup2-search a:active {
background: #ffb3e9 none repeat scroll 0 0 !important ;
color: #ffffff !important ;
}

div.popup2-search a {
background:transparent url('http://e.deviantart.com/emoticons/b/bulletwhite.gif') no-repeat 3px 4px !important ;
background-color: #bc1383 !important ;
color: #ffffff !important ;
padding-left: 23px !important ;
}

/* Userpage & message center poll */
div.ctube div.cfill {
    background:url(http://i225.photobucket.com/albums/dd93/irem58127/CSS/pollpink.png)!important;
}
div.ctube div.cfill span.sub10 {
color:#FFFFFF!important;
}

/* Footer poll */
.pollpole,
.poll-bar {
	background-color:#faf6f7!important;
	border-color:#b91080!important;
	color:#230118!important;
}
.pollpole {
	border-top:0px!important;	
	border-bottom:0px!important;	
	border-right:0px!important;
}
.poll_form .poll-bar i.tr,
.poll_form .poll-bar i.tl,
.poll_form .poll-bar i.br,
.poll_form .poll-bar i.bl {
	border-color:#d6ded4!important;
}


}
