@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/*---------------------------------------------------------
    Dropdown Bookmarks Toolbar (Stripped For Your Viewing Pleasure)
    by CraneTR
  Instead of having the entire bookmark toolbar visible
  at all times why not replace it with a button?
  ---------------------------------------------------------*/

/* hide stubborn bookmarks that want to be shown */
.places-toolbar-items .bookmark-item
  {visibility: collapse !important;}
#bookmarksBarContent > vbox
  {width: 0 !important;
   max-width: 0 !important;
   visibility: hidden !important;
   overflow: hidden !important;}

/* space button properly */
#personal-bookmarks
  {margin: 0 4px !important;}

/* change the chevron (>>) to a star */
#bookmarksBarContent
  {-moz-box-shadow: -moz-rgba(0,0,0,0.2) -0.04em 0.04em 1px 0px !important;
   min-width: 31px !important;
}

#bookmarksBarContent .chevron > image
  {display: none !important;}
#bookmarksBarContent .chevron
  {width: 22px !important;
   margin: 0 2px 0 0 !important;}
#bookmarksBarContent > hbox
  {background: url(data:image/png;base64,) #F7F7C2 center center no-repeat !important;
   -moz-box-shadow: inset #F4E877 0 8px 6px -6px !important;
   border-left: 3px #F4E87C double;
   border-right: 3px #F4E87C double;
   border-bottom: 1px #F4E87C double;}
#bookmarksBarContent > hbox:hover
  {border-color: #65CEF8;}


/* personalize the dropdown - you can add an image if that tickles your fancy */
#bookmarksBarContent menupopup
  {margin: 10px 0 0 -5px !important;
   padding: 0 !important;
   border: #FFF 4px solid !important;}
#bookmarksBarContent .chevron > menupopup > hbox
  {padding: 3px 0 !important;
   border: #CFCFCF 4px solid !important;
   text-align: center !important;
   background: #EEE !important;}
#bookmarksBarContent menupopup menu.bookmark-item,
#bookmarksBarContent menupopup menuitem.bookmark-item
  {padding: 1px 4px !important;}

/*
     If you don't like the dropdown and each of 
     your bookmarks have unique icons you could 
     just comment out the other stuff and keep 
     the text below (removes the text only).
.bookmark-item .toolbarbutton-text
  {display: none !important;}
*/
