@-moz-document domain("eldarya.fr"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.pl"), domain("eldarya.de"), domain("eldarya.dk"), domain("eldarya.es"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.nl"), domain("eldarya.ro"), domain("eldarya.ru"), domain("eldarya.se"), domain("eldarya.uk")

/*-Map music 2023 - code by Kalyna-*/
{
#map-container .map-location-tooltip .energy-required,
#map-container .map-location-tooltip .time-to-explore {
    left: -75px;
    top: 10px;
}

.pet-interface-container #treasure-hunt-interface .lens {
    border-radius: 15px;
    background: rgba(247, 243, 245, .89);
    position: relative;
    border: none;
    left: 0;
    z-index: 3;
    cursor: pointer;
    text-align: center;
}

#map-location-tooltips .map-location-tooltip .map-location-infos {
    position: absolute;
    text-align: left;
    background: none;
    height: 62px;
    top: 50%;
    margin-top: 10px;
    transform: translateY(-50%);
    width: 159px;
    border-radius: 0 24px 24px 0;
    box-shadow: none;
    left: 40px;
    padding-left: 25px;
    box-sizing: border-box;
}

.map-location-tooltip .map-location-infos {
    width: 500px;
    font-size: 11px;
    box-shadow: none;
    left: 65px;
    top: 55px;
    z-index: 20;
    background: rgba(255, 255, 255, 0);
    ;
}

#map-location-tooltips > div.map-location-tooltip.active > div.tooltip-bg {
    background: none;
}

#map-location-tooltips .map-location-tooltip .map-location-name {
    position: absolute;
    left: 0px;
    top: 40px;
    width: 200px;
    transform: translate(0, -80%);
    color: #000;
    font-family: "Alegreya Sans", sans-serif;
    font-size: 15px;
    font-weight: bold;
    text-shadow: 0 0 4px #fff;
    padding: 3px;
    text-align: center;
    z-index: 10;
}

