    input[type="tel"],
    input[type="text"],
    input[type="time"],
    input[type="url"],
    input[type="week"],
    textarea {
        background-color: #5d5d5d;
    }
    .btn-default,
    .vui-dropdown .dropdown-toggle,
    .vui-popup.vui-dialog .dialog-footer button,
    .vui-popup.vui-dialog.vui-message-dialog .message-dialog-footer button,
    .vui-stack-view .stack-view-header .edit,
    .vui-wizard .wizard-btn {
        color: white;
        background-image: -webkit-linear-gradient(top, #5d5d5d 0, #2d2d2d 100%);
        background-image: -o-linear-gradient(top, #5d5d5d 0, #2d2d2d 100%);
        background-image: linear-gradient(to bottom, #5d5d5d 0, #2d2d2d 100%);
    }
    .vui-validation-banner {
        background-color: #5d5d5d;
        border: white;
        color: #c2c2c2;
    }
    .stack-view-table .stack-new .stack-view-cell:first-child {
        background-color: #1b6a35 !important;
    }
    .alert-danger, .alert-error {
        color: orange;
    }
    .notification-area .alert-error a {
        color: white;
    }
    .notification-area .alert-error {
        border: 1px solid orange;
    }
}
