css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.warez-bb.org) {

input.post,textarea.post,input.button,input.liteoption, input.mainoption,input.catbutton{-moz-appearance:none!important}

input.post,textarea.post,input.button[name*="keywords"]{
background:#fff !important;border:1px solid #9B9B9B !important;
font-size:10px; font-family:Verdana,Arial,Helvetica,sans-serif;padding: 4px !important; -moz-border-radius: 5px; color: #000 !important;}

textbox:focus, textarea:focus, input:focus
{
  -moz-box-shadow: #6DA4DD 1px 1px 1px;
  -moz-box-shadow: #6DA4DD 1px 1px 1px, #6DA4DD 1px 1px 1px, #6DA4DD 1px 1px 11px, #6DA4DD 1px 1px 11px;  
}

select:focus, input:not([type="checkbox"]):not([type="radio"]):focus
{
  -moz-appearance: none !important;
}

.postdetails{
color: #000 !important;
}

/* Links used in the site, user rank colors on main page looks fine with it, thanks DI :) */
a:link:not([style]):not(:hover),a:active,a:visited:not([style]):not(:hover),a.postlink, b > a:link:not([style]):not(:hover), b > a:active:not(:hover), b > a:visited:not(:hover), b > a.postlink:not(:hover){color:#242424 !important;text-decoration:none; border: 0 !important;}
a:hover, b > a:hover {color:#000000 !important;}

/* The largest text used in the index page title and topic title etc. */
.maintitle,h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif;text-decoration:none;color:#242424 !important}

/* The largest text used in the index page title and topic title etc. PS: Come as guest thingy */
.header_maintitle,h1{font:bold 20px/120% "Trebuchet MS",Verdana,Arial,Helvetica,sans-serif; text-decoration:none; color:#242424 !important}

/* This style will apply to the subtext of the forum below comes as guess.. e.g. Sex is better than... :o */
.header_genmed{color:#242424 !important}

.explaintitle{color:#242424 !important}

/* Outermost area of the forum */ 
body:not(.gaybody){background:#41576b !important;font-size:12px; font-family:Verdana,Arial,Helvetica,sans-serif;margin:6px;padding:0;}

/* Second outermost area of forum. The one used between actual forum area box and outmost area. Well tbh, try changing color to black to see clearly that part of the forum */
.bodyline:not(.gaybodyline){background: #d7e2ef repeat-x !important;background-repeat:repeat;border:1px solid #313131 !important;}

/* The red rounded rectangle where they show you PM notification. Thanks to glenny for reporting and its fixed now. (: */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div > div > p:not(.gtfodarktheme) { color: #000 !important; }

/* Forums, Topics, Post, Last Post text style... took me ages to find this.. :( */
table.forumline > tbody > tr > th {color:#242424 !important}

/* Advertisement area rounded rectangle and powered by phpBB rounded rectangle */
table.bodyline > tbody > tr > td > table > tbody > tr > td > div:not(.gtfodarktheme) {
	background: #4e647b !important;
	border: 1px solid #A0A0A0 !important; -moz-border-radius: 8px; }
	
/* The footer ads rounded rectangle and Time, Queries stats rectangle */
.footerline{background: #415367 repeat-x !important;background-repeat:repeat;border:1px solid #888 !important;-moz-border-radius: 6px;}

/* This is the gradient background at the top of the page (the logo section)*/
.topbkg/*:not([style*="750k_logo"])*/{background: #BABABA url(http://i50.tinypic.com/2hzivy0.jpg) repeat-x !important} /*images/cellpic_bkg.png*/

/* The top navigation bar which shows Search, Profile, Logout etc. */
.topnav{font-size:10px;background: #E3EBEF url(http://i46.tinypic.com/2jaj4o5.jpg) repeat-x !important; /*images/cellpic_nav.gif*/
height:21px;white-space:nowrap;border: 0px solid #71b9de !important;border-width: 1px 0px 1px 0px}

/* Guess what, its our logo! You can even change logo position to center by increasing padding */
table.topbkg td a[href*="index.php"] > img:not([src*="750k_logo"]) {
	height: 110px !important;
	width: 0 !important;
	padding-right: 290px !important;
	background: url(http://i34.tinypic.com/am2xlc.jpg) no-repeat right;	
}

/* This is the gradient background at the top of the page. The one where text like Forum, Topics, Posts, Last Post is written.*/
th/*:not([style*="750k_logo"])*/{background: #496076 !important} /*cellpic3.png*/

/* The style applied to Category name in the forum. e.g. Listing, Reuests. */
td.cat/*:not([style*="750k_logo"])*/{background: #496076 !important} /*cellpic1.png*/

/* The one for the first... umm nvm. Figure out these yourselves. I can't really explain them. :( */
.row1{ background : #d7e2ef  repeat-x !important }
.row2, .helpline{ background : #bfd0e6  repeat-x !important ;}
.row3 { background : #acc1df repeat-x !important ;}

/* Space row between 2 posts */
td.spacerow{background:#496076 !important}

.errorline{background: #cad9ea !important;
color: #000 !important}


tr[id*="cat_id_"]:hover > td.row1{
	background: #98b4d6 !important;
}
tr[id*="cat_id_"]:hover > td.row2{
	background: #98b4d6 !important;
}
tr[id*="cat_id_"]:hover > td.row3{
	background: #98b4d6 !important;
}

