/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.google.cl/firefox?client=firefox-a&rls=org.mozilla:es-ES:official") 
{

/** delete background of google top bar*/
.gbtc .gbtb{display:none !important;}
#doc3{margin-top:-30px !important;}
#gbx1,#gbx2,#gbx3,#gbx4{background-color:transparent !important;border-bottom:0px !important}
#gbx1,#gbx2,.gbt a,.gbtcb{border-top:0px !important;}
.gbt a,.gbz0l .gbts{color:#FFF !important;text-shadow:0px 1px 3px #000 !important;}
.gbma{border-color:#FFF transparent transparent !important;}
.gbz0l .gbtb2{border-top:3px solid #FFF !important;margin-bottom:-2px !important;}
.gbto .gbma{border-color:#3366CC transparent transparent !important;}
.gbto .gbts{border-color:#FFF !important;text-shadow:none !important;}
.gbm{border:0 !important;}
.gbm{margin-top:-1px !important;}
.gbm .gbmt{color:#3366CC !important;text-shadow:none !important;}
.gbzt-hvr .gbtb2,.gbgt-hvr .gbtb2{border-top:2px solid #FFF !important;margin-bottom:-1px !important;opacity:0.6 !important;}
#gbi5{background-position:0px -20px !important;}
#gsea{padding-top:40px !important;}


/** body*/
#frame
{
  width:90% !important;}
body {

  background: #262626 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAE/CAYAAACHEqngAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABUSURBVHjaYmJgYPjPBCQYmAiyiFBCBIsSxWSqw8YlUwlOMWpLUFsJmYppbMrAE2B//CPswX94ff5v2AUJfU2hcTagjgR1ShBqF25ULG1JK/kBAgwAT3T9gBTuL/MAAAAASUVORK5CYII=") repeat-x !important;

  color: #fff !important;

}




/* default link color */
a:link, a:visited, a:hover, a:active, .link, a font[color="#008000"] {
  color: #acc7e5 !important;
}

/* default text color */
div, td { color: #fff !important; }

/* disable dashed focus outline */
:focus { outline: none !important; }

/* reset web form colors */
input *, textarea *, .aAutoComplete {
  background-color: -moz-Field !important;
  color: -moz-fieldtext !important;
}

/* fix for fade-in effect on front page */
#fctr, #fmsg, #ghead, #sbl, .promo, .fade {
 background: transparent !important;
}


/* Google bar "menu" and Google Suggest
---------------------------------------*/
#gbi, #gbg, .gac_m {
  background: #1b222b !important;
  border-color: #313e4e !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0, 0.5) !important;
}

a.gb2:hover,
.gac_b .gac_c,
.gac_b .gac_d {
  background: #3f4f64 !important;
  color: #fff !important;
}

.gbd { border-color: #4b5f78 !important; }
.gbh { border: none !important; }

.gac_a > .gac_c { color: #fff !important; }

.gac_d, .gac_e { color: rgba(255,255,255,0.5) !important; }

/* ad URL */
.gac_h { color: #d8d4ac !important; }



/* Search results
-----------------*/
/* big link */
#res .g .r a {
  color: #acc7e5 !important;
}

/* big link visited */
#res .g a:visited {
  opacity: 0.5 !important;
}

/* link description */
#res .g .s {
  color: #fff !important;
}

/* URL text */
.a, cite, .cite, .cite:link {
  color: #d8d4ac !important;
}

/* cached and similar links */
#res .g .s .gl a {
  color: #ccc !important;
}

/* visited cached and similar links */
#res .g .s .gl a:visited {
  opacity: 0.6 !important;
}

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* sub page links (on first link result) */
#res .s .slk a {
  color: #acc7e5 !important;
  opacity: 0.8 !important;
}

/* visited sub page links  */
#res .s .slk a:visited {
  opacity: 0.5 !important;
}

/* larger link title */
#res .l { font-size: 1.1em !important; }

/* more line spacing on descriptions */
#res .s { line-height: 1.4 !important; }

/* image thumbnails */
#res .g img {
  border: none !important;
  -moz-box-shadow: 4px 4px 10px rgba(0,0,0,0.4) !important;
}

/* File type (ex: [PDF]) next to link */
.g .w { color: #ff6 !important; }

/* "File Format" */
.f { color: #ffc !important; }


/* "video results" time info */
#res .g .ts font[color="#666666"] {
  color: #ffc !important;
}

/* "more options" */
.tbo #tbd {
  background: transparent !important;
  border-color: #acc7e5 !important;
}

/* Related searches bar */
#tbt5 {
  background: #303c4c url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAACJCAIAAABilMVrAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABcSURBVHjalJAxDsAwCAM5/pI5e///sEYqS4qdqgMiAgcO55hXRpBErLyCyjJob5S+arQ/Srv3cXoMx7ZLcVaPDw4xF8vBwZcXj+HG+uE8o83jxy2nu63nT/0WYACIxgHLMP/PmAAAAABJRU5ErkJggg==) repeat-x !important;
  border-color: rgba(172,199,229,0.1) !important;
}

/* source code listing */
#res tt { color: #fff !important; }



/* Ads
------*/
#mbEnd, #tads { background: rgba(68,86,107,0.4) !important; }

#mbEnd {
  border-left: 1px solid transparent !important;
}

/* hide search result ads (delete next line if you want them) */
#mbEnd, #tads { display: none !important; }

/* Google Suggest ads */
.gac_n { display: none !important; }



/* Bars
-------*/

/* results bar */
#ssb,
form[action="/movies"] tr[bgcolor="#e5ecf9"],
#canvas > .heading  {
  background: #2a3542 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAaCAIAAAD0eNHBAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABPSURBVHjatJCxDQAhDMSOF5MxASOwf4/ocgyAkULxKS3rIrm0PnTcJ7q/aJUN1EjDAXTOhQvkOiL97bLALu5K6OY7vDVjV/m+pF46bAEGAAT6LPtxqpMMAAAAAElFTkSuQmCC) repeat-x !important;
  border-top-color: #536982 !important;
}

/* big bar after bottom pager */
#bsf,
#navbar + br + table td[bgcolor="#e5ecf9"] {
  background: #303d4c url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAABkCAIAAAA0W+b9AAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABnSURBVHjaYrRwi2DABpgYcAB6SLD8Z/iPXQKHODl2MOAwiyzn4rLj/3+ahxUuO3C6iop2kBMkVIta3EbhiQ+apxIWHEFF1QTHQIf88Z8OGYd6ziW9LBnQEm5UYlRiVGJUghoSAAEGAOYBHq+LOtzMAAAAAElFTkSuQmCC) repeat-x !important;
  border-top-color: #475a70 !important;
  border-bottom-color: #354353 !important;
}

/* hide borders */
.k { background: none !important; }

/* hide old pager images */
#navbar td img { visibility: hidden !important; }

/* -------------------------------------------------------------------------------------------- */

div[style*="/images/firefox/gradsprite2.png"] {
  background-image: url(data:image/png;base64,) !important;
}
div[style*="none"] {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAYAAABytg0kAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAgSURBVHjaYjxz5kwAAwMDAwsDA0M8AwMDAwAAAP//AwAswwMZlFi3aQAAAABJRU5ErkJggg==") ! important; 
height: 2px  ! important; 
} 
td[bgcolor="#ababab"]{
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAIAAAACCAYAAABytg0kAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAbSURBVHjaYjxz5sx6BgaGhUwMUAAAAAD//wMANlIDuMi7pZgAAAAASUVORK5CYII=") ! important; 
min-width: 2px !important; 
max-width: 2px !important; 
}

td[bgcolor="#b0b0b0"]{
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAABCAYAAAD5PA/NAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAgSURBVHjaYvz//z/D2bNnAxgYGOIZGBgWAgAAAP//AwBMrga2jzsYJQAAAABJRU5ErkJggg==") ! important; 
margin-left: 10px !important; 
min-width: 3px !important; 
max-width: 4px !important; 
} 


div[style*="/images/firefox/sprite2.png"] {
  background-image: url(http://i164.photobucket.com/albums/u8/halcon_rs/sprite4.png) !important;
}
}
