css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {

/* General */
body, ul#sr-bar li a, #sr-bar a, .link .rank {font-family: Helvetica, Arial, sans-serif !important;}
a {color: #06C !important;}
a:hover, a:active {color: #09F !important;text-decoration: underline !important;}
/* Body */
body {line-height: 1.25 !important;}

/* Header */
#header {margin-right:0 !important;}

#sr-header-area {background-color: #F3F3F3 !important;border-bottom: 1px solid #CCC !important;}

#sr-bar a {color: #666 !important;padding: 0 5px !important;background-color: #F3F3F3 !important;border-left: 1px solid #FFF !important;border-right: 1px solid #E3E3E3 !important;line-height:1.5em !important;font-size: 10px;}
#sr-bar a:hover, #sr-bar a:active {background-color: #FFF !important;border-color: #DDD !important;color: #09F !important;}
#sr-bar .separator {display:none !important}
#sr-bar .selected a {font-weight: bold !important; color: red !important}

#sr-more-link {color: #555 !important;letter-spacing:1px;top:4px !important; z-index: 200;font-size: 11px;}

#header-bottom-left {width:80% !important; line-height: 20px !important; padding:0 !important;margin-left: 18px !important}
#header-bottom-left a#header-img-a img#header-img {}

.tabmenu {}
.tabmenu li a {padding: 2px 6px 0px 6px !important;}
.tabmenu li.selected a {background-color: #FFF !important;border-color: #5F99CF #5F99CF #FFF #5F99CF !important;}

#header-bottom-right {text-align: right}
#header-bottom-right {-moz-border-radius:0 !important; padding: 0 !important; width: 20% !important;line-height: 20px !important; background-color:#CEE3F8 !important}
#header-bottom-right .user {margin-right: 1em !important;}
#header-bottom-right .separator {display:none;}
#header-bottom-right #mail {margin-right: 1em !important}
#header-bottom-right .flat-list {margin-right: 1em !important}
#header-bottom-right .logout {margin-right: 1em !important}

.link {margin: 0 0 0.5em 0 !important;padding:0.5em 0.5em 0 0.5em !important; border: 1px solid #f9f9f9 !important;-moz-border-radius:3px !important;}
.link:hover {background-color: #eee; border-color:#aaa}
.link .rank {margin-top: 10px !important;margin-right: 0px !important; font-weight: bold !important; color: #DDD !important;}
.link:hover .rank {color: #888 !important}
.link .score {margin-top: 2px !important;line-height: 1 !important;}
.link .midcol {padding: 1px 0 !important;border: 1px solid #EEE !important; margin-top: 2px !important; margin-right: 10px !important; width: 17px !important}
.link:hover .midcol {border-color:#ddd !important}
.link .title {font-size:1.25em !important; margin: 0 !important;}
.link .domain {margin-left: 0.2em !important}
.link .domain a {color: #999 !important;}
.link .flat-list {float: left !important; padding: 2px 0 !important; margin-top:0.5em !important;}
.link .flat-list li {padding-right: 2px !important}
.link .flat-list li a{border-bottom:0 !important;}
.link .tagline {float: right !important;color: #999 !important;margin: 0.5em 0 0 0 !important;}
.link .tagline a {color: #888 !important;font-weight: bold !important;}
.link .tagline a:hover, .tagline a:focus {color: #666 !important;}


.thing .title.loggedin:visited {color:#551A8B !important;}

.arrow {margin: 1px auto 0 !important;}
.arroe.up {background-position: -4px -223px !important;}
.arrow.up:hover {background-position: -4px -179px !important;}
.arrow.down {background-position: -4px -245px !important;}
.arrow.down:hover {background-position: -4px -201px !important;}



.entry .buttons li a {padding: 1px 2px !important;border: 1px solid #DDD !important;color: #AAA !important;background: #F6F6F6 none !important; -moz-border-radius-topleft:2px !important; -moz-border-radius-topright:2px !important; -moz-border-radius-bottomleft: 2px !important; -moz-border-radius-bottomright:2px !important;}
.entry .buttons li a:hover, .entry .buttons li a:active {border-color: #AAA !important;text-decoration: none !important;color: #09F !important;background: #FFF none !important;}

.sidebox.submit .spacer, .sidebox.create .spacer {margin-left: .5em !important;}

.organic-listing {display: none !important;}

#siteTable {padding-top: .75em !important;}
#siteTable .link {margin-left: 2em !important;margin-right: 2em !important;}

#search input[type="text"] {border-color:#999 !important;width: 95% !important;padding: 2px !important; -moz-border-radius: 3px !important}

.login-form-side {border: 1px solid #DDD !important;margin: 0 5px !important;padding: 3px 0 !important;background: #F9F9F9 none !important;-moz-border-radius:5px !important;}
.login-form-side input {border: 1px solid #999 !important;width: 124px !important;margin: .5em 0 0 8px !important;padding: 2px 0 !important;font-size: 13px !important;height: auto !important;}

#rem-login-main {width: auto !important;margin: .2em 0 0 2px !important;}

.login-form-side label {margin-left: 2px !important;color: #666 !important;}

#remember-me a {display: block !important;text-align: center !important;padding: 1px 2px !important;border: 1px solid #DDD !important;margin: .5em .25em 0 .25em !important;color: #888 !important; -moz-border-radius:2px !important;}

#remember-me a:hover, #remember-me a:active {color: #09F !important;border-color: #888 !important;background-color: #FFF !important;}


.content {float: left; width: 80%; margin: 0 !important;}

.side {margin: 0 !important; padding: 0 !important; width: 20% !important;border: 0px solid #DDD !important;-moz-border-radius:0 !important}
.side .spacer {margin: .75em 0 1em 0 !important;}
.side .content {float:none !important; -moz-border-radius: 3px !important; width: 93% !important}

.usertext, .side .bottom {width: 96% !important}

.titlebox .bottom {}

.morelink {margin: 0 !important;-moz-border-radius:5px !important; width: 96% !important}
.morelink a {font-size: 13px !important;}
.morelink a:hover, .morelink a:active {color: #FFF !important;text-decoration: none !important;}
.morelink .nub {display: none !important;}

.sidebox .spacer {padding: 0 0 0 44px !important;}
.sidebox .subtitle {font-size: 9px !important; color: #666 !important; margin-left: 1px !important;}

.usertext-body li {list-style: none !important}
.usertext-body li p {margin:0 !important}
.usertext-body li a {display:block; line-height: 1.5em !important}
.usertext-body li a:hover {background-color: #efefef}

.sidecontentbox .content {border: 0 !important}
.sidecontentbox .content a {margin-left: 20px !important; line-height: 1.5em !important}
.sidecontentbox .content a:hover {background-color: #efefef}

#remember-me button.btn {background-color: #FFF !important;background-image: none !important;border: 1px solid #888 !important; color: #444 !important; -moz-border-radius-bottomleft: 2px !important; -moz-border-radius-topleft: 2px !important; -moz-border-radius-topright: 2px !important; -moz-border-radius-bottomright: 2px !important;font-family: Helvetica, Arial, sans-serif !important;font-size: 11px !important;font-weight: bold !important;}

.commentarea{margin-left:20px !important}
.usertext-edit {width: 100% !important}
.usertext textarea {width: 100% !important; margin-left: 30px !important; max-width: 61em !important; padding: 0 5px !important}
.usertext-edit .bottom-area {margin-left: 30px !important}

.comment .child {border-left: 1px dashed #336699 !important;}
.comment .child .child {border-left: 1px dashed #B7B7B7 !important;}
.comment .child .child .child {border-left: 1px dashed #336699 !important;}
.comment .child .child .child .child {border-left: 1px dashed #B7B7B7 !important;}
.comment .child .child .child .child .child {border-left: 1px dashed #336699 !important;}
.comment .child .child .child .child .child .child {border-left: 1px dashed #B7B7B7 !important;}
.comment .child .child .child .child .child .child .child {border-left: 1px dashed #336699 !important;}
.comment .child .child .child .child .child .child .child .child {border-left: 1px dashed #B7B7B7 !important;}
.comment .child .child .child .child .child .child .child .child .child {border-left: 1px dashed #336699 !important;}
.comment .child .child .child .child .child .child .child .child .child .child {border-left: 1px dashed #B7B7B7 !important;}

.searchpane {margin:5px 0px 5px 0 !important;}
.infobar {margin:5px 0px 5px 0 !important;}

.nextprev{padding-left: 20px !important}
}
