css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* GENERAL */
#editBookmarkPanel {
-moz-appearance: none !important;
border-image-source: url("data:image/png;base64,") !important;
border-image-slice: 26 50 22 20 fill !important;
border-image-width: 26px 50px 22px 20px !important;
border-image-repeat: repeat !important;
-moz-window-shadow: none !important;
margin-right: -36px !important;
opacity: 1.0 !important;
}
#editBookmarkPanelContent {
width: 300px !important;
}
/* REMOVE PANEL OVERLAY */
#editBookmarkPanel > box > box,
#editBookmarkPanel > box > box > box {
border: none !important;
background: none !important;
color: #fff !important;
box-shadow: none !important;
}
#editBookmarkPanel > box > box {
margin: 5px 5px 5px !important;
}
#editBookmarkPanel > box > box > image {
display: none !important;
}

/* INPUT FIELDS */
#editBMPanel_tagsField,
#editBMPanel_namePicker[droppable="false"] > .menulist-editable-box {
background: #fff !important;
color: #000 !important;
box-shadow: none !important;
}

/* BIG STAR ICON */
#editBookmarkPanelStarIcon {
list-style-image: url("data:image/png;base64,")
!important;
opacity: 0.4 !important;
}

/* BUTTONS */
#editBookmarkPanelBottomButtons > button,
#editBookmarkPanelRemoveButton {
-moz-appearance: button !important;
background: none !important;
border: none !important;
color: #000 !important;
font-size: 8pt !important;
min-width: 76px !important;
min-height: 19px !important;
text-shadow: none !important;
box-shadow: none !important;
}

/* FOLDER MENULIST */
#editBMPanel_folderMenuList {
-moz-appearance: menulist !important;
padding: 0px !important;
background: none !important;
border: none !important;
box-shadow: none !important;
color: #000 !important;
text-shadow: none !important;
}
.menulist-dropmarker {
display: none !important;
}

/* FOLDER TREE */
#editBookmarkPanel #editBMPanel_folderTree,
#editBookmarkPanel #editBMPanel_newFolderBox,
#editBookmarkPanel #editBMPanel_tagsSelector {
margin-left: -55px !important;
margin-right: 0px !important;
border: solid 1px #777 !important;
border-radius: 0px !important;
background: #333 !important;
color: #fff !important;
}
#editBookmarkPanel #editBMPanel_folderTree > stack,
#editBookmarkPanel #editBMPanel_tagsSelector > listrows {
background: #333 !important;
}
#editBookmarkPanel #editBMPanel_newFolderBox {
background: none !important;
border: none !important;
}
#editBookmarkPanel #editBMPanel_newFolderButton {
border: none !important;
background: none !important;
list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAICAYAAADED76LAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAHolAACAgwAA+f8AAIDpAAB1MAAA6mAAADqYAAAXb5JfxUYAAAAsSURBVHjaYvz//z8DEoBxGGECTAwEAEEFLEjGYrOKOBMYaetIAAAAAP//AwCSsgcQQxrbgAAAAABJRU5ErkJggg==")
!important;
margin-top: -1px !important;
opacity: 0.5 !important;
}
#editBookmarkPanel #editBMPanel_newFolderButton:hover:active {
opacity: 0.4 !important;
}
/* DISCLOSURE TRIANGLES */
#editBMPanel_folderTree treechildren::-moz-tree-twisty {
-moz-appearance: none !important;
list-style-image: url("data:image/png;base64,")
!important;
-moz-image-region: rect(0px 7px 9px 0px) !important;
width: 7px !important;
}
#editBMPanel_folderTree treechildren::-moz-tree-twisty(open) {
-moz-image-region: rect(0px 25px 9px 16px) !important;
width: 9px !important;
margin-left: -2px !important;
}
/* TREE ROW */
#editBookmarkPanel
#editBMPanel_folderTree treechildren::-moz-tree-cell-text,
#editBookmarkPanel:-moz-system-metric(mac-graphite-theme)
#editBMPanel_folderTree treechildren::-moz-tree-cell-text {
color: #fff !important;
}
/* SELECTED ROW */
#editBookmarkPanel
#editBMPanel_folderTree treechildren::-moz-tree-row(selected) {
background: -moz-linear-gradient(top, #6f81f5, #3f51f2) repeat-x !important;
border: none !important;
color: #fff !important;
}
/* SELECTED ROW (GRAPHITE) */
#editBookmarkPanel:-moz-system-metric(mac-graphite-theme)
#editBMPanel_folderTree treechildren::-moz-tree-row(selected) {
background: -moz-linear-gradient(top, #7f868d, #69717c) repeat-x !important;
border: none !important;
color: #fff !important;
}

/* EXPANDERS */
#editBookmarkPanel #editBMPanel_foldersExpander,
#editBookmarkPanel #editBMPanel_tagsSelectorExpander {
-moz-appearance: none !important;
background: none !important;
border: none !important;
outline: none !important;
list-style-image: url("data:image/png;base64,")
!important;
box-shadow: none !important;
}
#editBookmarkPanel .expander-up {
-moz-image-region: rect(0px 18px 18px 0px) !important;
}
#editBookmarkPanel .expander-up:hover:active {
opacity: 0.75 !important;
}
#editBookmarkPanel .expander-down {
-moz-image-region: rect(0px 36px 18px 18px) !important;
}
#editBookmarkPanel .expander-down:hover:active {
opacity: 0.75 !important;
}

/* HIDE SCROLLBARS */
#editBookmarkPanel #editBMPanel_folderTree scrollbar[orient="vertical"] {
opacity: 0.0 !important;
margin-left: -15px !important;
}
