css:
@namespace url(http://www.w3.org/1999/xhtml);

/* bitly ad */
#include > form > p:nth-child(2) {display: none !important}

/* AddToAny Toolbar */
  [src="http://static.addtoany.com/buttons/share_save_256_24.png"] {display: none !important;}
/* AddToAny Dropdown */
  #a2apage_dropdown {display: none !important;}

/* AddThis Toolbars (on multiple sites) */
  #socialBotAdd, .addthis_button, .addthis_toolbox.addthis_default_style, #addthis_link, [onclick="return addthis_sendto()"]
 {display: none !important;}

/* Hello Stumblers! Greet Block */
#greet_block {display: none !important;}

/* Meebo Toolbars */
  .meebo-00
   {display: none !important;}

/* Wibya Toolbars */
  #wibiyaToolbar, #wibiyaToolbar_window_template
   {display: none !important;}

/* Remove Preview Popups from Wordpress Sites */
@-moz-document domain(wordpress.com)
  {#snap_com_shot_main {display: none !important}}

/* Reuters Blank ad Space and Trackbar toolbar */
@-moz-document domain(reuters.com)
  {
   .grid4 {width: 200px !important;}
   .gridlined4 {background: none !important;}
   #trackbar {display: none !important;}
  }

/* CNet Toolbar */
#cnetToolbarWrap {display: none !important;}

/* Associated Content Minibar */
@-moz-document domain(associatedcontent.com)
{#minibar {display: none !important;}}

/* CBS Toolbar */
@-moz-document domain(cbsnews.com)
{#cbsSMPB {display: none !important;}}

/* HuffingtonPost Bar */
@-moz-document domain(huffingtonpost.com)
{#service_bottom_bar {display: none !important;}}

/* Remove SoundCloud Comment Popups */
@-moz-document domain("soundcloud.com")
{#timestamped-comment-template {display: none !important;}}

/*** Physorg ad ***/
@-moz-document domain("physorg.com") 
{.one-ad {display: none !important;}}

/* mediafire "MediaPro" popup */
@-moz-document domain("mediafire.com")
{#catfish_div {display: none !important;}}

/* Cracked.com "sliver bars" */
#fb_sliver {display:none !important;}

/* Wikia notifications */
#WikiaNotifications {display: none !important;}

@-moz-document domain("1up.com")
{#status-bar {display: none !important;}}

/* TBS.com left side bar */
@-moz-document domain("tbs.com")
{#leftclick {display: none !important;}}

/* AfterDawn tooltip advertisements */
@-moz-document domain("afterdawn.com")
{#iTt {display: none !important}}

/* 1-UP.com Status/Network bars */
@-moz-document domain("1up.com")
{#status-bar, #network-bar {display: none !important}}

/* Wikia floating footer */
@-moz-document domain("wikia.com")
{#WikiaFooter {display: none !important}}

/* Gizmodo Permalink Ads */
@-moz-document domain("gizmodo.com")
{.permalink_ads {display: none !important}}

/* Spike.com floating footer */
@-moz-document domain("spike.com")
{.outer_activity_bar {display: none !important}}

/* Patheos header bar. */
@-moz-document domain(patheos.com)
{#wpadminbar {display: none !important}}
