css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(patamap.com) {
  table.mapAffi > tbody > tr > td,
  table.casedecoupe {
    height: 30px !important;
    width:  30px !important;
  }

  table.mapAffi > tbody > tr > td > div > div div {
    background-size: 100% 100%;
  }

  table.casedecoupe > tbody > tr {
    height: 15px !important;
  }

  table.casedecoupe > tbody > tr > td,
  table.casedecoupe > tbody > tr > td > img {
    height: 15px !important;
    width:  15px !important;
  }

  table.casedecoupe > tbody > tr > td > img[src^='imageshordes/NumberHordes/'][src$='.png'] {
    margin: -8px -4px 0 0 !important;
    height: 8px !important;
    width: 5px !important;
  }

  img[src^='imageshordes/legend_'][src$='.png'] {
    height: 20px;
    width:  20px;
  }
}
