css:
@-moz-document url-prefix("https://jira."), url-prefix("https://jira."), domain("jira.")
{
/* Раскраска доп. полей в карточке задачи */
.ghx-issue .ghx-extra-fields .ghx-extra-field { 
    background-color: /*[[color-hint]]*/;
    border-radius: 3px !important; 
    padding: 0 6px;
    margin: 4px 0px;
}

/* Скрыть футтер в карточке: тип, приоритет, естимейт */
.ghx-issue .ghx-card-footer .ghx-flags,
.ghx-issue .ghx-card-footer .ghx-end {
    display: none !important;
}

/** Боковая полоса в Таске*/
.ghx-issue {
    border-left: 4px solid /*[[color-blue]]*/;
}

/* Боковая полоса в Суб-Таске*/
.ghx-issue-subtask {
   border-left: 4px solid /*[[color-orange]]*/;
}

/* Боковая полоса в группе задач*/
.ghx-swimlane-header {
   border-left: 4px solid /*[[color-blue]]*/;
}

/* Боковая полоса в группе задач. Дефолт*/
.ghx-swimlane-default {
    border-left: none;
}
}
