/*
	==== MyAnimeList.net - Night Theme by Zealotus ====
	Once based on MyAnimeList.net Dark Theme (styleID:5263) by Mathes (UserID:6092)
	But a lot has changed since then ;)

	==== Bookmarks ====
	[Z-RplImg] Bookmark for replacement images and icons.
	[Z-ColFix] Bookmark for my colour fixes.
	[Z-PbrRpl] Bookmark for "progress" bars replacement colours.

	==== Full Change Log ====
	3.0.0 - Rewrite! Colour changes only!
	3.0.1 - Release! + Fixed site header.
	3.1.1 - New Layout, WOO. . . *headache!* >.<;;
	3.1.2 - Minor tweaks.
	3.1.3 - Fixed the new divs.
	3.1.4 - Fixed the borked Nav.
	3.1.5 - Fixed status bar colours.
	3.1.7 - Fixed #rightcontentunder.
	3.1.8 - Includes Ad Blocker (again).
	==== End Change Log ====
		
		
		==== Quick Tweak Help ===
	Find and replace: 458 to #FE66CB, 	#FF96FB
			         (bars & text)	     (links)
	To easilly change the color of all the blue text in the theme.
	Borders are either #333 or #444
	Backgrounds are #111, #1A1A1A, #222, #090909, #1F1F1F
	Regular texts #DDD and #AAA, #fff, #03030 in some rare cases
	
	*/
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document url(http://myanimelist.net/), url-prefix(http://myanimelist.net/login), url-prefix("http://myanimelist.net/addtolist"), url-prefix("http://myanimelist.net/panel"), url-prefix("http://myanimelist.net/profile"), url-prefix("http://myanimelist.net//profile"), url-prefix("http://myanimelist.net/editprofile"), url-prefix("http://myanimelist.net/myfriends"), url-prefix("http://myanimelist.net/clubs"), url-prefix("http://myanimelist.net/mymessages"), url-prefix("http://myanimelist.net/myreviews"), url-prefix("http://myanimelist.net/myrecommendations"), url-prefix("http://myanimelist.net/blog"), url-prefix("http://myanimelist.net/myblog"), url-prefix("http://myanimelist.net/anime.php"), url-prefix("http://myanimelist.net/manga.php"), url-prefix("http://myanimelist.net/people"), url-prefix("http://myanimelist.net/character"), url-prefix("http://myanimelist.net/fansub-groups"), url-prefix("http://myanimelist.net/topanime"), url-prefix("http://myanimelist.net/topmanga"), url-prefix("http://myanimelist.net/favorites"), url-prefix("http://myanimelist.net/reviews"), url-prefix("http://myanimelist.net/news"), url-prefix("http://myanimelist.net/forum/"), url-prefix("http://myanimelist.net/users"), url-prefix("http://myanimelist.net/manga/"), url-prefix("http://myanimelist.net/anime/"), url-prefix("http://myanimelist.net/modules"), url-prefix("http://myanimelist.net/showreview"), url-prefix("http://myanimelist.net/about"), url-prefix("http://myanimelist.net/donations"), url-prefix("http://myanimelist.net/tou.php"), url-prefix("http://myanimelist.net/privacy"), url-prefix(http://myanimelist.net/dbchanges), url-prefix(http://myanimelist.net/comments), url-prefix(http://myanimelist.net/friends), url-prefix(http://myanimelist.net/showclubs), url-prefix(http://myanimelist.net/editclub), url-prefix(http://myanimelist.net/topusers), url-prefix(http://myanimelist.net/shared), url-prefix(http://myanimelist.net/history), url-prefix(http://myanimelist.net/recommendations), url-prefix(http://myanimelist.net/info.php), url-prefix(http://myanimelist.net/comtocom.php), url-prefix(http://myanimelist.net/register), url-prefix(http://myanimelist.net/admin), url-prefix(http://myanimelist.net/password), url-prefix(http://myanimelist.net/editlist) {*{/*$*/}
	/* Global Defaults */
	body, #myanimelist, #content, #fancy_bg {
		color:					#DDD !important;
		background:				#111 !important;
		margin:					0 auto !important;
	}
	#content, #contentHome, .container, #rightcontentunder {
		border:					0 !important;
		background:				transparent !important;
	}

	/* Headers */
	#header, #headerSmall {
		display:				none !important;
	}
	.inboxsent_header {
		border-color:			#444 !important;
		background-color:		#FE66CB !important;
		color:					#030303 !important;
	}
	.normal_header {
		border-color:			#333 !important;
		color:					#FE66CB !important;
	}

	/* * Navigator * */
	/* Main */
	#nav li a, #nav a:visited {
		color:					#DDD !important;
		background:				#FE66CB !important;
	}
	#nav li a:hover, #nav a:active, #nav li.hover a {
		color:					#DDD !important;
		background:				#333 !important;
	}
	#nav li .new a, #nav li .new a:visited, #nav li .new a:active, #nav li .new a:hover, .count {
		font-weight:			bold !important;
		color:					#F33 !important;
	}

	/* Sub */
	#nav ul a, #nav ul a:visited {
		color:					#DDD !important;
		background:				#333 !important;
	}
	#nav ul a:hover, #nav ul a:active {
		background:				#FE66CB !important;
	}
	#menu_left, #menu, #menu_right, #menu_left #nav>li>ul>li>span a:hover {
		background:				#FE66CB !important;
	}

	/* Input Text Fields */
	.inputtext, .textarea {
		border-color:			#333 !important;
		background-color:		#1F1F1F !important;
		color:					#FFF !important;
	}
	.inputtext:hover, .textarea:hover  {
		background-color:		#090909 !important;
	}

	.inputButton, .button {
		border-top-color:		#666 !important;
		border-left-color:		#666 !important;
		border-bottom-color:	#222 !important;
		border-right-color:		#222 !important;
		background-color:		#FE66CB !important;
	}

	h1 {
		border-top:				0 !important;
		border-left:			0 !important;
		border-right:			0 !important;
		border-bottom:			solid 1px #FE66CB !important;
		background:				#222 !important;
		color:					#FE66CB !important;
	}

	h2 {
		color:					#FE66CB !important;
		border-bottom:			solid 1px #333 !important;
	}

	.mini_header {
		background-color:		#222 !important;
		color:					#FE66CB !important;
		border-bottom:			solid #FE66CB 1px !important;
	}

	/* Links and Texts */
	a,a:visited {
		color:					#FF96FB !important;
	}
	a:focus {
		outline:				none !important;
	}
	a:hover {
		color:					#FE66CB !important;
		text-decoration:		none !important;
	}
	.lightLink, .lightLink:link, .lightLink:visited, .lightLink:active {
		color:					#666 !important;
	}
	.dark_text, .dark_text:hover,.dark_text:visited , .dark_text_normal, .dark_text_normal:hover,.dark_text_normal:visited  {
		color:					#FE66CB !important;
	}
	.bgColor1 .dark_text, .bgColor1 .dark_text:hover, .bgColor1 .dark_text:visited, .bgColor1 .dark_text_normal,
		.bgColor1 .dark_text_normal:hover, .bgColor1 .dark_text_normal:visited  {
		color:					#FE66CB !important;
	}
	.colored_text {
		color:					#333 !important;
	}

	/* Horizontal Navs */
	#horiznav_nav {
		border-color:			#FE66CB !important;
	}
	#horiznav_nav li {
		background-color:		#111 !important;
	}
	#horiznav_nav ul li a {
		color:					#FE66CB !important;
		background-color:		#222 !important;
	}
	#horiznav_nav ul li a:hover {
		background-color:		#FE66CB !important;
		color:					#FFF !important;
	}
	#horiznav_nav ul li .horiznav_active {
		background-color:		#FE66CB !important;
		color:					#FFF !important;
	}

	/* Results */
	.goodresult {
		color:					#AAA !important;
		background-color:		#121 !important;
		border-color:			#4A4 !important;
	}
	.goodresult a:link,.goodresult a:active,.goodresult a:visited {
		color:					#347 !important;
	}
	.badresult {
		color:					#AAA !important;
		background-color:		#211 !important;
		border-color:			#A44 !important;
	}
	.badresult a:link,.badresult a:active,.badresult a:visited  {
		color:					#347 !important;
	}

	/* Profile */
	.profile_leftcell {
		border:					0 !important;
	}
	#profileRows a {
		color:					#FE66CB !important;
		font-weight:			bold !important;
		border-color:			#333 !important;
	}
	#profileRows a:hover {
		color:					#FFF !important;
		background-color:		#FE66CB !important;
		border-color:			#333 !important;
	}

/* Friends */
 .friendHolder {
  background-color:  #222 !important;
 }
 .bgLightBlue {
  background-color:  #4c0033 !important;
 }

	/* Useless borders... */
	.borderClass {
		border:					0 !important;
	}

	/* Messages */
	.row_highlighted {
		background-color:		#222 !important;
		border-color:			#FE66CB !important;
	}
	.row_not_highlighted {
		background-color:		#111 !important;
		border-color:			#333 !important;
	}	

	/* Info bubble */
	.hoverinfo {
		color:					#FFF !important;
		background-image:		none !important;
		background-color:		#111 !important;
		border-color:			#FE66CB !important;
		border-style:			solid !important;
		border-width:			1px !important;
		z-index:				9999 !important;
	}

	.borderDark {
		border-color:			#333 !important;
		overflow:				auto !important;
	}
	.borderLight {
		border-color:			#333 !important;
		overflow:				auto !important;
	}

	div[style="margin: 0pt 10px 10px 0pt; float: left; display: none;"] {
		border:					1px solid #333 !important;
	}
	div[style="border: 1px solid rgb(190, 190, 190); margin: 4px 2px 2px; padding: 2px;"] {
		border-color:			#333 !important;
	}
	div[style="border: 1px solid rgb(235, 235, 235); margin: 4px 2px 2px; padding: 1px;"] {
		border-color:			#333 !important;
	}

	.bgbdrContainer {
		background-color:		#111 !important;
		border:					1px solid #333 !important;
	}

	.bgColor1 {
		background-color:		#1A1A1A !important;
	}
	.bgColor2, .bgColor, .bgNone {
		background-color:		#222 !important;
	}

	div.picSurround img {
		background-color:		#111 !important;
		border:					1px solid #333 !important;
	}

	.button_form {
		background-color:		#1A1A1A !important;
		border:					none !important;
	}

	.button_form:hover {
		background-color:		#222 !important;
		border:					none !important;
	}

	#dialog {
		border:					10px solid #111 !important;
	}
	#dialog td {
		border:					1px solid #333 !important;
	}

	#copyright {
		/*display:				none !important;*/
		color:					#666 !important;
	}
	#copyright:after {
		content:				' Night Theme by Zealotus.' !important;
	}

	#panel_left {
		border-right:			0 !important;
	}

	.vote_container {
		background:				#CBB !important;
	}
	.message_separator {
		background:				#522 !important;
	}
	.forum_boardrow1 {
		background:				#111 !important;
		border:					#333 !important;
	}
	.forum_boardrow2 {
		background:				#222 !important;
		border:					#333 !important;
	}
	.forum_boardrowspacer {
		background:				transparent !important;
	}
	.forum_category {
		background:				#FE66CB !important;
	}
	.forum_category a {
		color:					#FFF !important;
	}
	#forum_options_container ul li {
		border-color:			#FE66CB !important;
	}
	.quotetext, .codetext {
		background:				#222 !important;
		border:					1px solid #333 !important;
	}
	.sig {
		border-color:			#333 !important;
	}

	/* Shared Anime/Manga page fix */
	td[style='background-color: rgb(246, 246, 246);'] {
		background-color:		#222 !important;
	}
	span[style='color: rgb(0, 0, 255);'] {
		color:					#04F !important;
	}
	span[style='color: rgb(255, 0, 0);'] {
		color:					#E10 !important;
	}
	span[style=""] {
		color:					#2D2 !important;
	}

	/* ==== Feature Bookmarks ==== */
	
	/* Change "New" topic replies advisory on forum board */
	table[id=forumTopics] .forum_boardrow1 strong a:first-child:before {
		content:				'New ' !important;
		color:					#FE66CB !important;
	}
	table[id=forumTopics] .forum_boardrow2 strong a:first-child:before {
		content:				'New ' !important;
		color:					#FE66CB !important;
	}
	
	
	/* ==== [Z-RplImg] START ==== */
	/* The icons */
	img[src*="images/good-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/good-on.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/bad-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
