	div[style*="background-color:#8aa7d8"]	{ background-color: #f293df !important; }
	div[style*="background-color:#7c9acf"]	{ background-color: #f08bd9 !important; }
	div[style*="background-color:#7191c8"]	{ background-color: #ee83d4 !important; }
	div[style*="background-color:#6687c0"]	{ background-color: #ec79ce !important; }
	div[style*="background-color:#5b7db8"]	{ background-color: #ea71c9 !important; }
	div[style*="background-color:#5073af"]	{ background-color: #e969c4 !important; }
	div[style*="background-color:#476aa6"]	{ background-color: #e662c0 !important; }
