css:
@-moz-document domain("app.asana.com/0/home/1201960694165003")
{
/* Sidebar Left color */
  .Sidebar
  {
    background-color: white;
  }

  .SidebarItemRow, .SidebarItemRow:link, .SidebarItemRow:visited
  {
    color: inherit;
  }

  .SidebarTopNavLinks
  {
    color: white;
  }

  .SidebarCollapsibleHeader
  {
    color: #6f7782;
  }

  .SidebarTeamMembershipHeader-teamName,
.SidebarTeamMembershipHeader--collapsed .SidebarTeamMembershipHeader-teamName
  {
    color: black;
  }

  .SidebarTeamDetailsAddMenuButton-button
  {
    color: rgba(0,0,0,.5);
    fill: rgba(0,0,0,.5);
  }

  .SidebarTeamMembershipHeader-zipIcon
  {
    fill: rgba(0,0,0,.5);
  }

  .SidebarTeamMembershipHeader-zipIcon:hover
  {
    fill: rgba(100,100,100,.5);
  }

  .SidebarItemRow-menuIcon.is-dropdownVisible, .SidebarItemRow:hover .SidebarItemRow-menuIcon, .SidebarItemRow.is-selected .SidebarItemRow-menuIcon
  {
    color: rgba(0,0,0,.5);
    fill: rgba(0,0,0,.5);
  }

  .SidebarItemRow-menuIcon.is-dropdownVisible:hover, .SidebarItemRow-menuIcon.is-dropdownVisible:focus, .SidebarItemRow:hover .SidebarItemRow-menuIcon:hover, .SidebarItemRow:hover .SidebarItemRow-menuIcon:focus, .SidebarItemRow.is-selected .SidebarItemRow-menuIcon:hover, .SidebarItemRow.is-selected .SidebarItemRow-menuIcon:focus
  {
    fill: #000;
  }

  .SidebarItemRow.is-selected:focus, .SidebarItemRow.is-selected
  {
    background-color: rgba(200, 200, 200, 0.5);
  }

  .SidebarItemRow.is-selected:hover, 
.SidebarItemRow:hover, .SidebarItemRow:focus, .DragContainer--isDragging .SidebarItemRow
  {
    background-color: rgba(150, 150, 150, 0.5);
  }
}
