ont .touch .see-all-link-icon:hover, .Snell Roundhand-font .touch .see-all-link-icon:link, .Snell Roundhand-font .touch .see-all-link-icon:visited, .gotham-font .touch .see-all-link:active, .Snell Roundhand-font .touch .see-all-link:hover, .Snell Roundhand-font .touch .see-all-link:link, .Snell Roundhand-font .touch .see-all-link:visited
  {
    color: #A399CC;
  }

  #navbar-search-input, #navbar-search-btn
  {
    border: 1px solid #A399CC;
    ;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.pinimg.com/564x/7f/61/2d/7f612d42b91f4ae8983b742948f34f86.jpg) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #A399CC, #A399CC);
  }

  #ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: #A399CC;
  }

  .notification-red
  {
    background-color: #A399CC;
    padding: 1px 6px;
    min-width: 18px;
  }

  .notification-blue
  {
    background-color: #A399CC;
    padding: 1px 6px;
    min-width: 18px;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(163, 153, 204, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(163, 153, 204, 0.5);
  }

  .avatar-back
  {
    background-image: url(https://i.pinimg.com/564x/7f/61/2d/7f612d42b91f4ae8983b742948f34f86.jpg);
  }


/* other roblox stuff*/

  .light-theme .rbx-header
  {
    background-color: #ffffff;
    border: solid #A399CC;
    border-width: 0 0 1px;
  }


/* profile */

  .light-theme .section-content
  {
    background-color: #A399CC;
    color: #fffff;
  }

  .light-theme .rbx-tabs-horizontal .nav-tabs
  {
    background-color: transparent !important;
  }

  .light-theme .rbx-tab .rbx-tab-heading
  {
    background-color: transparent !important;
  }

  .light-theme .slide-switcher .slide-item-container .slide-item-container-left
  {
    background-color: #A399CC;
  }

  .light-theme .profile-avatar-right .profile-avatar-mask
  {
    background-color: #A399CC;
  }

  .light-theme .thumbnail-2d-container
  {
    border: 0;
    background-color: transparent !important;
  }

  .light-theme .avatar .avatar-card-image
  {
    background-color: #A399CC;
  }

  .light-theme .avatar-container .avatar-card-image, .light-theme .avatar-container img
  {
    background-color: #A399CC;
  }

  .light-theme .layer
  {
    background-color: #A399CC;
  }


/* catalog stuff + buy robux button*/

  .light-theme .btn-growth-md
  {
    background-color: #A399CC;
    border-color: #ffffff;
    color: #fff;
    border-radius: 100px;
  }

  .light-theme .touch .btn-growth-md.active, .light-theme .touch .btn-growth-md:active, .light-theme .touch .btn-growth-md:focus, .light-theme .touch .btn-growth-md:hover, .light-theme .touch .btn-growth-md:visited, .light-theme .touch .btn-growth-md.active, .light-theme .touch .btn-growth-md:active, .light-theme .touch .btn-growth-md:focus, .light-theme .touch .btn-growth-md:hover, .light-theme .touch .btn-growth-md:visited
  {
    color: #fff;
    background-color: #A399CC
    border-color #ffffff;
  }
  
/* BTR */
  .light-theme .rbx-tabs-vertical .category-tabs .menu-vertical
  {
    background-color: #A399CC;
    font-family: 'Snell Roundhand', sans-serif;
  }
