/* ==UserStyle==
@name           ROBLOX New Logo Replacement 2.0 ( for windows chrome)
@namespace      roblox.com
@version        7.2.0
@description    A style that replaces the ROBLOX logo with the old one.
@author         gustavo242
==/UserStyle== */


@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com"), url-prefix("https://forums.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://forums.roblox.com"), domain("roblox.com")
{
/*ROBLOX New Logo Replacement 2.0 by Gustavo- https://www.roblox.com/users/1878060599/profile

/*Should replace the new logo on the website with the old one*/
/*NOTE: Roblox deleted the "R" logo ("https://www.roblox.com/images/Logo/roblox_logo.svg")..*/
  .icon-logo
  {
    /*Main site navbar full logo*/
    background-image: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;
  }

  .icon-logo-r
  {
    /*Main site navbar small logo*/
    background-image: url("data:image/svg+xml;base64,") !important;
    background-size: 28px 160px !important;
    background-position-x: 2px !important;
    background-position-y: center !important;
  }

  .big-logo
  {
    /*Download page logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    height: 80px !important;
  }

  .robloxLogo
  {
    /*Landing page small logo*/
    content: url("http://i.imgur.com/Hj502As.png");
    /*hides the new logo in the worst way possible idk*/
    background-image: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;
    background-size: 167px 167px !important;
    background-position-x: left !important;
    background-position-y: center !important;
    width: 31px;
  }

  #MainLogoImage
  {
    /*Landing page big logo*/
    content: url("https://www.roblox.com/images/Logo/roblox_logo.svg") !important;
    margin-left: 20px !important;
  }

  #LogoAndSlogan .clearfix::after
  {
    /*Landing page slogan under the logo*/
    content: "Powering Imagination" !important;
    color: white !important;
    font-size: 35px !important;
    background-color: rgba(0, 0, 0, 0.7) !important;
    padding: 10px 25px !important;
    text-align: center !important;
    margin-top: -20px !important;
    display: inline-block !important;
  }

  #LogoAndSlogan .clearfix::before
  {
    /*Slogan trademark thing, needed here so the font can be smaller*/
    content: "â„¢" !important;
    color: white !important;
    position: absolute !important;
    font-size: 15px !important;
    top: 100px !important;
    margin-left: 413px !important;
  }

  .dark-theme .icon-default-logo-r,
.dark-theme .icon-logo-r,
.dark-theme .icon-logo-r-95
  {
    background-image: url(https://vignette.wikia.nocookie.net/logopedia/images/1/19/ROBLOX_Logo_2015.svg/revision/latest?cb=20170907130237);
  }

  .light-theme .icon-default-logo-r,
.light-theme .icon-logo-r,
.light-theme .icon-logo-r-95
  {
    background-image: url(https://vignette.wikia.nocookie.net/logopedia/images/1/19/ROBLOX_Logo_2015.svg/revision/latest?cb=20170907130237);
  }
}

@-moz-document url-prefix("https://blog.roblox.com"), url-prefix("https://corp.roblox.com/"), url-prefix("http://corp.roblox.com"), url-prefix("http://blog.roblox.com")
{
/*ROBLOX Blog*/
  footer .comp-logo a
  {
    /*Blog footer logo*/
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 30px !important;
  }

  header .site-title a
  {
    /*Blog header logo*/
    background-image: url(https://www.roblox.com/images/logo/roblox_logo.svg) !important;
    background-size: 110px !important;
    height: 42px !important;
  }
}

@-moz-document url-prefix("https://www.roblox.com/account/signupredir?"), url-prefix("https://web.roblox.com/account/signupredir?"), url-prefix("https://www.roblox.com/"), url-prefix("https://web.roblox.com/"), url-prefix("https://de.roblox.com"), url-prefix("http://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("http://de.roblox.com"), url-prefix("https://de.roblox.com/account/signupredir?"), url-prefix("http://www.roblox.com/account/signupredir?"), url-prefix("http://web.roblox.com/account/signupredir?"), url-prefix("http://de.roblox.com/account/signupredir?")
{
/* signup page */
  #signup-header .text-logo
  {
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg);
    width: 90%;
    height: 65px;
    max-width: 457px;
  }
}

@-moz-document domain("roblox.com")
{
/*Events*/
  .logo-link > img
  {
    content: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 125% !important;
    margin-top: -15% !important;
  }
}

@-moz-document url-prefix("https://web.roblox.com/giftcards"), url-prefix("https://www.roblox.com/giftcards"), url-prefix("https://de.roblox.com/giftcards"), url-prefix("http://web.roblox.com/giftcards"), url-prefix("http://www.roblox.com/giftcards"), url-prefix("http://de.roblox.com/giftcards")
{
/*  giftcards */
  .logo-link > img
  {
    content: url(https://www.roblox.com/images/Logo/roblox_logo.svg) !important;
    background-size: cover !important;
    height: 125% !important;
    margin-top: -15% !important;
  }
}

@-moz-document url-prefix("https://en.help.roblox.com/"), url-prefix("http://en.help.roblox.com/"), url-prefix("http://careers.roblox.com/"), url-prefix("http://careers.roblox.com/")
{
/* ROBLOX Support */
  #header
  {
    background-color: #0074bd;
    height: 40px;
  }
/* Logo */
  .logo img
  {
    width: 0;
    max-height: 30px;
  }

  .logo a::after
  {
    display: inline-block;
    background-image: url(https://www.roblox.com/images/Logo/roblox_logo.svg);
    background-size: 100%;
    background-repeat: no-repeat;
    content: 'invisible';
    font-size: 0;
    width: 120px;
    height: 30px;
    position: absolute;
    left: 6px;
    top: 6px;
  }
}
