css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("turnitin.com") {

/* Main Background */
body    {
        background-color: #ff0066 !important;
        color: #000!important;
        }


/* Links */
a:link  {
        color: #660099 !important;
	}


/* "Welcome" text */
#top p	{
	font-size: 1.1em;
	padding: .5em 0 0 2em;
	width: 20em;
	font-weight: bold;
        color: #ff0066 !important;
	}

/* "Welcome" background */
#top	{
	height: 2.7em;
	width: 50em;
	z-index: 10;
	border-bottom: 1px solid #888;
	border-right: 1px solid #888;
	border-top: 1px solid #eee;
	position: relative;
	background: black !important;
	}

/* Top navigation menu */
#top_nav	{
	z-index: 20;
	font-weight: bold;
	position: absolute;
	top: 0;
	right: 0;
	}
/* Top Nav Menu link box background */
#top_nav a:link, #top_nav a:visited {
	text-decoration: none;
	color: white;
	padding: 0;
	background: #ff0066 !important;
	padding: .4em 6px .5em 6px;
	}
/* Top Nav Menu no links boxes */
.pad    {
        background: #ff0066 !important;
        }

/* "inactive" text */
.AR9    {
        color: #000000;
	}


/* "replies/new/last_reply/moderator"*/
.AR11   {
        color: #000000;
	}

/* Footer */
#footer_left	{
	position: absolute;
	bottom: 1.8em;
	border-bottom: 1px solid #888;
	border-top: 1px solid #888;
	width: 49em;
	background: #ff0066 !important;
	font-size: .8em;
	z-index: 100;
	border-right: 1px solid #888;
	}
#footer_right	{
	position: absolute;
	bottom: 1.8em;
	border-bottom: 1px solid #888;
	border-top: 1px solid #888;
	background: #ff0066 !important;
	font-size: .8em;
	width: 100%;
	}
#footer_left p	{
	padding: 0 0 0 2.8em;
	line-height: 2em;
        color: #999999 !important;
	}
#footer_right p	{
	background: #ff0066;
	width: 43em;
	margin-left: 51em;
	line-height: 2em;
	}
#footer_right a	{
	color: #666 !important;
	text-decoration: none;
	padding: 0 .5em;
	border-right: 1px solid #888;
	}

}
