css:
@-moz-document domain("c.mi.com") {
body {
	
	--color-primary: #ff6700;
    --color-primary-light: #d29d79;
    --color-primary-dark: #86512d;
    --color-border: #dcdee2;
    --color-border-light: #e7e7e7;
    --color-title: #ddd;
    --color-content: #999;
    --color-content-light: #fff;
    --color-content-dark: #17233d;
    --color-content-active: #4284f5;
    --color-content-disabled: #999;
    --color-background: #222;
    --color-background-light: #1a1a1a;
    --color-background-grey: #f5f5f5;
    --color-background-lightgrey: #fafafa;
    --color-background-deepgrey: #666;
    --color-background-dark: #ccc;
    --color-background-active: #5485ed;
    --color-disabled: #e7e7e7;
    --color-disabled-dark: #aaa;
    --color-disabled-light: #f5f5f5;
    --color-divider: #333;
    --color-info: #2db7f5;
    --color-success: #19be6b;
    --color-warning: #f90;
    --color-error: #ed4014;
    --color-shadow: #e6e7e8;
    --color-message-bg: rgba(51,51,51,0.04);
    --color-message-hoverbg: rgba(51,51,51,0.02);
    --color-message-unreadtext: #fff;
    --color-message-unreadbg: #fc7b37;
    --color-message-preview: rgba(51,51,51,0.5);
    --color-message-navbg: #fbfbfb;
    --color-icon-grey: #8f8f8f;
}

/*Тёмный фон, светлый текст*/
body, #hd, #hd .hd-menu, .thread-list, .checked, .xm-box:not([class*="thread-box"]), .interbar .rightbox, .interbar .country-select .hd-menu-list-wrapper, .interbar, .inter-bar, .nav-bar, header, .nav-bar.fixed, .micomm-tabs, .columnBox, #fctrl_taskWindow, .columnBox {background-color: #1a1a1a; color: #ddd;}

.interbar .country-select #targetCountry, .thread-list .xm-text-loading, .account-info .hd-menu-list-ul, .news-item, .column-info, .column-list, .fwin .taskListBox, .tedt .bar {background-color: #222; color: #ddd;}
.xm-side-box .checkin-box .dayMisstionBox .allMissionComplete, .xm-side-box .checkin-box .dayMisstionBox .todayMissionBox, #fwin_content_taskWindow, .xm-pagenav .pg a, .xm-pagenav .pg label .px {background-color: #222;}

.top-seperator, .xm-side-box .users-box .seperator, .greyLine, hr.l, .pgbtn a {background: #333;}
.xm-side-box .featured-box .seperator, #ft, .xm-side-box .thread-author-box .author-medal, .column-recommend, .subject-content+.subject-content, .xm-threads-list>li, .xm-tabs>li, .userList .userItems, .column-list-ci-item, .column-info-title, .filter-time, .sectionFilter {border-color: #333;}
.avatar-container .user-avatar-special {border-color: #1a1a1a;}

#scrolltop, .tedt {background: #222; border: none;}
#scrolltop a {border: none;}

#hd .hd-menu:before {border-color: transparent transparent #1a1a1a;}

.psth {background: #222; border-radius: 3px 0 0 3px; position: relative;}
.psth::after {content: ''; position: absolute; right: 0px; top: 0px; border: 14.75px solid transparent; border-right: 14.75px solid #1a1a1a;}

.truste-consent-track-class {background-color: #2a2a2a; border:none; box-shadow: 2px 2px black;}
#truste-consent-text {color: white;}
#truste-consent-text a {color: var(--color-primary);}
#truste-consent-text a:hover {color: white;}

.quote, .related-sections {background-color: #333 !important; border-radius: 5px; color: white;}
.related-item {background-color: #222; border-color: #555; border-radius: 5px;}
.related-item-header {color: var(--color-primary);}
.related-item-header:hover, .tabContent li a {color: white;}
.tabContent li .num, .fwin .taskListBox table .desc, .tedt a {color: #ddd !important;}

.profile-section .profile-node-list-ul>li {background-color: #222; border-radius: 3px;}

.tedt .fpd a {background-color: #222; box-shadow: none; border: none;}
.tedt .fpd a:hover {box-shadow: 0 0 2px black; background-color: #222;}

.tedt .area, .tedt .pt, .pgs .pg a {background: #2a2a2a;}
.tedt .bar {border: none;}

.column-nav .navI {filter: invert(1.0);}

.pn, .pn em, .column-info, .column-list {box-shadow: none;}
.pns .pn {border-radius: 3px;}
.pn em {background-color: #222;}
.pn.vm {background: #222; border: none;}

.xm-forum-info .forum-icon {border: none;}

/*Дополнительно для всплывающего окна*/
.fwin .taskListBox table .okBtn {filter: invert(1.0) hue-rotate(180deg);} /*Сделать новую иконку*/
.fwin .taskListBox table .getAwardBtn, .fwin .taskListBox table .normalBtn, .xm-pagenav .pg a, .xm-pagenav .pg label .px, .pgs .pg a {border-radius: 3px;}

#xmMetalPop {border-radius: 10px;}
.xm-metalpops .topBar {border-radius: 10px 10px 0 0;}
.xm-metalpops .popcontent {border-radius: 0 0 10px 10px; background-color: #222;}
.xm-metalpops .popcontent .title, .xm-metalpops .popcontent .subdes {color: #ddd;}
.xm-metalpops .popcontent .iconBox {background: none;}
.xm-metalpops .popcontent .iconBox img {filter: drop-shadow(0 0 5px white);}
.xm-metalpops .popcontent .metalBoxs {height: 140px;}

/*Заголовки и другое*/
.thread-list .thread-list-ul .thread-plain .content .title, .thread-list .thread-list-ul .thread-plain .thread-info .z .auth-name, .thread-list .thread-list-ul .column-display .column-box-header .column-header-title, .thread-list .thread-list-ul .column-box .column-title, .interbar .mi-community-text, .interbar .country-select .hd-menu-list-wrapper dt, .xm-side-box .checkin-box #calendar #calendar_week a, .tab-active, .xm-side-box .users-box .content .user-name, .xm-side-box .users-box ul .user-rank, h1, h2, h3, h4, h5, .xm-side-box .thread-author-box .name, .column-recommend-head .column-recommend-title, .column-recommend .column-recommend-title, .subject-content.other-floor .node-author .xi2, .userList .userItems .contentBox .content .username, .xm-tabs>li a, .newsTitle, .colum-list-title, .column-list-ci-item .listName, .column-info-title, .newsinfo1 .name {color: white !important;}

.xm-side-box .checkin-box #calendar .calendar_expire .checked_day, #hd .main-nav-ul>li .nav-item .hd-menu, font[color="#000000"], #subjecthide, .pg a, .pg strong, .pgb a, .pg label {color: #ddd !important;}

/*Создание тем (дополнительно)*/
.edt.editor-body {border-radius: 10px; border: none;}
.edt .bar {border-radius: 8px 8px 0 0; background: #2a2a2a;}
.edt .bbar {border-radius: 0 0 8px 8px; background: #2a2a2a; color: #ddd;}
.edt .b1r, .edt .b2r, .edt .btn, .edt .bbar {border-color: #2a2a2a;}
.bbs {border-color: #333 !important;}
.ntc_l, .edt .area {background: #2a2a2a;}
.edt .bar {border: none;}
.post-extra #post_extra_tb label.a, .post-extra #post_extra_tb label, #postbox .exfm {background-color: #2a2a2a;}
.post-extra #post_extra_tb label {border-color: #333 #333 #ff6700 #333}
.post-extra #post_extra_tb label.a {border-color: #ff6700 #ff6700 #333; border-radius: 10px 10px 0 0;}
.post-extra #post_extra_tb label {border-radius: 10px 10px 0 0;}
.post-extra #post_extra_c .exfm {border-radius: 0 10px 10px 10px;}
#postbox input, #postbox select, #postbox textarea {border-radius: 5px; border-color: #ff6700;}
#postbox .exfm {border-radius: 5px; border: none;}
#postbox .px {background: #333; border: none;}
.pnpost .btn-orange, .pnpost .btn-gray {border: none; border-radius: 3px;}
.b2r #e_fontsize {background-color: #2a2a2a; color: #ddd;}
#e_button div a {background-color: #333; border: none;}
#e_button div a:hover {filter: brightness(1.5);}
.b2r #e_fontsize {width: 67px; border-radius: 5px;}

/*Украшательства*/
.slide-image, .subject-content img, .tedt, .news-item {border-radius: 10px;}
.top-feature-img, .thread-list .thread-list-ul .column-box .column-cover, .thread-feature-img, .news-img {border-radius: 5px;}
.tedt .bar {border-radius: 6px 6px 0 0;}
.tedt .area {border-radius: 0 0 6px 6px;}
.top-recommand-thread .thread-item .text-shadow-mask, .column-list, .column-info {border-radius: 0 0 5px 5px;}
.xm-side-box .thread-box, .xm-side-box .qr-box .download, .threadreplystyle, .reply-text-area .pnc, .column-apply, .xm-threads-list>li .thread-pics .img a, .sectionFilter {border-radius: 3px;}
.category, #truste-consent-button, #truste-consent-required {border-radius: 2px;}
.fixsubthread .subTitleBox {border-radius: 0 0 3px 3px;}

.chat-container .chat-nochat-img {filter: invert(1.0);}

.news-item, .column-apply {box-shadow: none;}
.news-item:hover {box-shadow: 0 1px 4px black;}

.pl table.t_table, .pl table.t_table tbody tr td{border: none;}

/*Нет доступа*/
.nfl .f_c {background-color: #333; border-radius: 5px; border: none;}

/*Исправление ошибок*/
.column-recommend .recommend-img {background-size: cover; border: none; border-radius: 3px;} /*Заполняет картинку в миниатюре обложки колонки в теме*/
}
