css:
@-moz-document regexp("(.*)ranobelib.me/(.*)/v[0-9]+/c[0-9]+(.*)") {
  
  .ads {
  	display: none;
  }

  .reader-container .article-image {
    text-align: center;
  }

  body {
    background-color: #383838;
    color: #ffffff;
    line-height: 1.4;
    overflow-x: hidden;
  }

  .reader-container {
      max-width: 1280px !important;
      font-size: 18px;
      line-height: 1.4;
      text-indent: 1.2em;
  }

}
