@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("geocaching.com") {

	/* Replace the blue frownie with the legacy purple frownie */
	img[src$="icon_sad.gif"], img[src$="/images/logtypes/3.png"] {
		background: url(data:image/png;base64,) no-repeat transparent !important;
		height:0 !important;
		width: 0 !important;
		padding: 0 0 16px 16px !important;
		overflow: hidden !important;
		border: 0 !important;
	}

}
