css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.burbuja.info") {

td[id^="td_post_"] div:not([id])
{
display:block!important;
height: 43px !important;
overflow: hidden !important;
}

td[id^="td_post_"] div:not([id]):active
{
display:block!important;
height:auto !important;
overflow:auto !important;
}

/* Quita el margen abajo del mensaje, antes de botones para que se vean*/
td[id^="td_post_"] div[align] br
{
display:none !important;
}


}
