css:
@-moz-document domain("ferzu.com") {
/* latin-ext */
@font-face {
  font-family: 'Sintony';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(https://fonts.gstatic.com/s/sintony/v13/XoHm2YDqR7-98cVUET0tuu8o.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Sintony';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(https://fonts.gstatic.com/s/sintony/v13/XoHm2YDqR7-98cVUETMtug.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* This is the "upgrade for pics" */
.photostream-overquota {
	display: none;
}
.planitem, .planbasic {
    color: #000 ! important;
}
#planstable .planpremium h3 {
	color: #eee !important;
}
/* Base styles */
h1,h2,h3,h4,h5,h6,body,textarea,input,select,input[type="file"],.editor-field.click-wrapped,.notify-new-icon,.statbox-location,.statbox-unit,.statbox.color3,.slider-label,.picker-label,.ui-widget input,.ui-widget,.ui-widget select,.ui-widget textarea,.ui-widget button,.actionitems h3,.followers h3,h3.actionitem,.statbox,.statbox-location,.dashb-item {
font-family: Sintony,'Segoe UI',Ubuntu,Geneva,Verdana,Tahoma,Arial,"Helvetica Neue",Helvetica,sans-serif;
}
.notify-new-icon,.ui-dialog-title,#smartbanner .sb-info em,#smartbanner .sb-button,td.right h2,td.left h2,.dlgcontent h2,.home-features h2,.actionitems h3,.followers h3,h3.actionitem,h4,.head h3 {
	text-transform: initial;
	font-variant: small-caps;
}
.version-bar {
padding-bottom: 8px;
}
span.profile-icon {
	color: #eee;
}
/* Updates */
#newsfeed-updates-bar, a:hover #newsfeed-updates-bar {
    border-width: thick;
    border-style: inset;
    border-radius: 15px;
    background-color: burlywood;
}
#newsfeed-updates-bar {
    border-style: inset;
    color: sienna;
}
a:hover #newsfeed-updates-bar {
	border-style: outset;
}
/* Presence */
div.presenceBox {
    width: 10px;
    height: 10px;
    border-top: 1px solid #000;
    border-left: 1px solid #000;
    border-radius: 5px;
}
.presence.memberdetails {
    border-radius: 0 15px 15px 0;
}
.presence.online, .presenceBox.online {
    background-color: forestgreen;
}
.presence.away, .presenceBox.away {
    background-color: darkgoldenrod;
}

/* Post/update box */
.post-add {
    display: flex;
    align-items: center;
    justify-content: center;
}
.post-add input[type="submit"] {
	margin-top: 8px;
	padding-top: 4px;
	height: 2em;
}
.post-add .editor-field textarea {
    width: 85%;
	height: 28px;
}
.editor-field {
    width: 100%;
}
.hero-background {
  	background: #000;
}
#header,#logindisplay,.page {
	background: transparent;
}
.nav-counter {
	background: #15b9d5;
}
h1, h2, h3 {
    color: #15b9d5 ! important;
}
.t_Tooltip .t_ContentContainer {
    background: #222;
    color: #999;
}
.comments, table.message p, .post, .ptext, .ui-tabs-panel {
    overflow: hidden;
}
.main, #main {
    background-color: #333;
    color: #eee;
	padding: 4px 16px 24px 8px;
}
.miniprofile {
	color: #444;
}
.post-add {
    background-color: #666;
}
table td.left.navt, .ui-widget-content, table.messages tr {
    padding-left: .75em;
    background: #222 ! important;
}
.actionitems a, .ui-widget-content, table.messages tr, ul.languages li a, .editor-label label {
    color: #ccc;
}
textarea, input[type="text"], input[type="password"], input[type="file"], select, .comment, .comment-add, .ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, .ui-state-active a, .ui-state-active a:link, .ui-state-active a:visited {
    color: #fff;
    background-color: #000;
}
a {
    color: #F4A460;
}
td.msg-photo {
    border: none;
}
.jslider .jslider-value, .slider-label {
    color: #000;
}
.editor-field {
    color: #fff;
}
/* UI Icons */
[data-icon] {
    vertical-align: middle;
}
/* Bare UI pages */
#main>h1 {
    padding-top: .25em;
}
/* Search box */
form#formMessageSearch a span[data-icon] {
    vertical-align: middle;
    transform: scale(1.5);
}
/* Header icons */
h1 [data-icon], h2 [data-icon] {
    transform: scale(2);
margin-top: -.2em;
    padding: 0 .5em 0 .5em;
vertical-align: baseline;
    margin-right: .5em;
}
#main>h1 [data-icon] {
    margin-left: 1em;
    padding: .25em .5em;
}
.quicklists [data-icon], .actionitems [data-icon] {
    vertical-align: bottom;
    margin-right: .5em;
}
.profile-header [data-icon] {
    padding: initial;
    margin: auto;
}
/* At least DMs */
.topbar+h3 [data-icon] {
    transform: scale(1.5);
    vertical-align: baseline;
    margin-top: -3px;
}
/* I don't even understand why this button is busted */
.link-button2+.link-button1 {
    vertical-align: top;
}
/* Icons for rest of sheet! */
/* Newsfeed */
[data-icon="f"] {
content: url("data:image/png;base64,");
}
