css:
/* a */
a {
    color: hsl(174deg 100% 25%) !important;
}

/* buttons */
button [style*="background-color: rgb(255, 255, 255)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%));
    border: 1px solid #fff !important;
}

button [style*="background-color: rgb(255, 255, 255)"] [style*="color: rgb(43, 44, 51)"] {
    color: #fff !important;
}

/* profile buttons */
button [style*="background-color: rgb(241, 241, 244)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%));
}

button [style*="background-color: rgb(241, 241, 244)"] [data-text-as-pseudo-element] {
    color: #fff !important;
}

/* icons */
svg[viewBox="0 0 24 24"] {
    fill: #fff;
}

/* conversation list item */
[role=group] [role=button] [style*="background-color: rgb(43, 44, 51)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%)) !important;
    border: 1px solid #fff;
}

/* conversation list profile */
[role=group] [role=button] [style*="background-color: rgb(241, 241, 244)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%));
}

[role=group] [role=button] [style*="background-color: rgb(241, 241, 244)"] [style*="color: rgb(0, 0, 0)"] {
    color: #fff !important;
}

/* chat headings */
[role=region] [role=heading] [style*="background-color: rgb(241, 241, 244)"] {
    border: unset !important;
}

[role=region] [role=heading] [style*="color: rgb(241, 241, 244)"] {
    color: hsl(174deg 100% 25%) !important;
}

/* other chat background */
[role=region] [style*="background-color: rgb(241, 241, 244)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%));
    border: 1px solid #fff !important;
}

[role=region] [style*="background-color: rgb(241, 241, 244)"] [style*="color: rgb(43, 44, 51)"] {
    color: #fff !important;
}

/* my chat background */
[role=region] [style*="background-color: rgb(43, 44, 51)"] {
    background-color: transparent !important;
    border: 1px solid #fff !important;
}

[role=region] [style*="background-color: rgb(43, 44, 51)"] a {
    color: #fff !important;
}

/* chatbox */
[style*="background-color: rgb(43, 44, 51)"] {
    background-color: transparent !important;
    border: 1px solid #fff !important;
}

[style*="background-color: rgb(43, 44, 51)"] [style*="background-color: rgb(32, 32, 35)"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%)) !important;
}

[style*="background-color: rgb(43, 44, 51)"] button[style*="background-color: rgb(43, 44, 51)"] {
    border: unset !important;
}

/* send button */
button[title="Send message"] {
    display: none !important;
}

/* profile dialog */
[role=dialog] [style*="background: linear-gradient"] {
    background: linear-gradient(90deg, hsl(174deg 50% 10%), hsl(174deg 100% 15%)) !important;
}

[role=dialog] [style*="background-color: rgb(43, 44, 51)"] {
    background-color: rgb(43, 44, 51) !important;
    border: unset !important;
}

[role=dialog] button[title="Send message"] {
    display: flex !important;
}
