}
/*이전 게시판 차단(none or inline)*/
.prev_list {
display: inline;
}
/*상단, 하단 배경색*/
.footer,
.index #PCHeader .bg {
background: #2d2d2d !important;
}
/*=================================================================================*/
/*게시판 전체 크기*/
.index {
width: 100% !important;
min-width: 1180px !important;
background-color: #2d2d2d !important;
}
.index #container .contents .left_cont {
width: 1010px !important;
margin-bottom: 9px !important;
}
.index #container {
margin: 20px auto;
width: 1010px;
}
/*게시판 표 테이블 속성*/
.tbl_type01 {
width: 100%;
margin-bottom: 10px;
background: #3c3c3c;
border: none;
}
/*=================================================================================*/
/*게시판 글 목록 음영*/
.tbl_type01 tr:nth-child(odd) {
