}
/*게시판 헤드타이틀*/
.tbl_type01 thead tr td {
height: 44px;
border-bottom: 0px;
background: url(https://bg_bar01.gif) no-repeat left 50%;
color: #cdcdcd;
}
/*말머리 대괄호 추가 (불펜)
.tbl_type01 tbody tr td .word_bullpen:before {content:'['}
.tbl_type01 tbody tr td .word_bullpen:after {content:']'}*/
/*말머리 대괄호 추가 (불펜 外)
.tbl_type01 tbody tr td .word:before {content:'['}
.tbl_type01 tbody tr td .word:after {content:']'}*/
/*말머리 스타일 (불펜 外)*/
.tbl_type01 tbody tr td .word {
width: 48px;
color: #cdcdcd;
padding-right: 10px;
font-weight: bold;
text-align: left;
}
/*말머리 스타일 (불펜)*/
.tbl_type01 tbody tr td .word_bullpen {
width: 50px;
padding-right: 3px;
margin-left: 0px;
color: #cdcdcd;
font-weight: bold;
text-align: center;
}
/*본문 미디어 크기*/
#contentDetail img,
video {
width: auto !important;
max-width: 970px !important;
text-align: center !important;
}
/*추천,조회,리플 위치*/
.view_head .items .text {
display: inline-block !important;
padding: 0 10px 17px 10px !important;
width: 970px !important;
}
/* 하단 검색창*/
.list_search {
float: left !important;
height: 28px !important;
width: 350px !important;
padding: 0 20px 0 0 !important;
border: 0px!important;
background: #3c3c3c !important;
}
/*댓글수 색상*/
.tbl_type01 .replycnt {
color: #ff7f02 !important;
font-weight: bold !important;
}
/*댓글 입력 박스*/
.left_cont table {
width: 1010px !important;
}
#contentTable {
width: 95% !important;
margin: 0 0 0 18px !important;
color: #bebebe !important;
}
/*댓글 레이아웃 배경*/
.reply_list {
padding: 0px 0px 20px 0px !important;
}
/* 댓글 박스 여백 조정 */
.txt {
margin-top: -13px !important;
margin-bottom: -10px !important;
}
/*댓글 사진 크기*/
.reply_list .txt_box img,
video {
width: auto !important;
max-width: 970px !important;
text-align: center !important;
}
/*댓글 색구분 */
.reply_list .other_reply .txt:nth-child(odd) {
position: relative !important;
float: left !important;
max-width: 90% !important;
padding: 15px 10px !important;
background: rgba( 60, 60, 60, 0) !important;
border-radius: 0 10px 10px 10px !important;
text-overflow: ellipsis !important;
text-align: left !important;
font-family: '맑은고딕'!important;
}
/*댓글 설정*/
.reply_list .other_con:nth-child(odd) {
overflow: hidden !important;
position: relative !important;
margin: 0 0 0 0 !important;
padding: 10px 0 0 55px !important;
background: #3c3c3c !important;
}
.reply_list .other_con:nth-child(even) {
overflow: hidden !important;
position: relative !important;
margin: 0 0 0 0 !important;
padding: 10px 0 0 55px !important;
background: #4b4b4b !important;
}
/*댓글창 표 색상*/
.reply_list,
#cmtFormTable td {
background: #3c3c3c !important;
}
/*댓글 이모티콘 사이드 삭제*/
.reply_list .other_con .txt .icon_arr {
display: none !important;
}
/*댓글 닉네임 색상*/
.reply_list .other_reply .txt span,
.tbl_type01 thead tr td {
color: #cdcdcd;
border-bottom: 0px;
}
/*본문 작성자 댓글*/
.reply_list .other_reply .txt,
.reply_list .other_con .txt .icon_arr,
.reply_list .my_reply .txt {
background: #3c3c3c
}
/*댓글 이모티콘*/
.reply_list .other_con .photo img {
position: absolute !important;
left: 10px !important;
border-radius: 20% !important;
border: 0px !important;
}
.reply_list .other_con .photo {
position: absolute !important;
top: 10px !important;
left: 0 !important;
width: 37px !important;
height: 37px !important;
border: 0px solid!important;
}
/*본인 댓글 배경색 & 이모티콘*/
.reply_list .my_con {
overflow: hidden !important;
position: relative !important;
margin: 0 0 0 0 !important;
padding: 10px 0 0 55px !important;
background: #5a5a5a !important;
}
.reply_list .my_reply .txt {
float: left !important;
background: rgba( 90, 90, 90, 0) !important;
}
.reply_list .my_con .photo {
position: absolute !important;
top: 10px !important;
left: 10px !important;
width: 37px !important;
height: 37px !important;
border: 0px !important;
}
/*댓글 경계선*/
.reply_list .other_con {
border-bottom: 1px solid #2d2d2d !important;
}
/*닉네임 설정*/
.reply_list .other_reply .txt_box .name {
color: #cdcdcd !important;
font-weight: bold !important;
}
/*본인 닉네임 설정*/
.reply_list .my_reply .txt_box .name {
color: #cdcdcd !important;
font-weight: bold !important;
}
.reply_list .my_con {
border-bottom: 0px !important;
}
/* 댓글 작성일시 & 아이피 주소 색상과 글꼴 변경 */
.reply_list .date,
.reply_list .ip {
display: inline;
color: #afafaf !important;
font-size: 11px !important;
font-family: Lucida Console !important;
}
/*댓글 아이피 괄호*/
/*.reply_list .ip:before {content:''}
.reply_list .ip:after {content:''}*/
/*=================================================================================*/
/*담장 제목 없애기*/
.tit_today {
display: none !important;
}
/* 담장 타이틀 스타일 */
.main_contents .nav .bestTitle {
color: #cdcdcd !important;
text-align: center !important;
font-size: 100% !important;
background: #3c3c3c !important;
padding: 1px 0 2px 1px !important;
font-family: '맑은고딕' !important;
}
/*담장 타이틀 배경*/
.main_contents .nav li {
position: relative;
width: 308px;
padding: 0 18px 0;
float: left;
height: 40px;
margin-right: -1px;
font-weight: bold;
border-right: 1px #3c3c3c;
background: #3c3c3c !important;
}
/*담장 글목록 배경속성*/
.lists_today {
float: left;
margin: 0 -1px 0 0;
padding: 12px 18px 12px;
width: 308px;
border-right: 1px #4b4b4b;
border-top: 0;
border-bottom: 0;
