height: 47px;
padding-top: 7px;
text-align: center;
background: #2d2d2d;
border: none;
margin-bottom: 10px;
}
/*하단 현재 페이지*/
.page strong {
display: inline-block;
padding: 10px;
color: #cdcdcd;
font-weight: bold;
}
/*하단 페이지*/
.page a {
display: inline-block;
padding: 10px;
color: #bebebe;
}
/*하단 페이지 이전, 다음 버튼*/
.page a.right {
width: 47px;
height: 28px;
padding: 0;
background: url(https://i.imgur.com/ub9rCRm.png) no-repeat 0 -32px;
vertical-align: middle;
}
.page a.left {
width: 47px;
height: 28px;
padding: 0;
background: url(https://i.imgur.com/ub9rCRm.png) no-repeat 0 0;
vertical-align: middle;
}
/*하단 검색폼*/
.list_search {
float: left !important;
height: 30px !important;
width: 350px !important;
padding: 0 20px 0 0 !important;
border: 0px!important;
background: #3c3c3c !important;
}
.list_search span select {
float: left;
padding: 6px;
border: none;
color: #bebebe;
background-color: #3c3c3c !important;
font-size: 12px;
}
.list_search span input {
float: left;
width: 228px;
font-size: 12px;
height: 56;
line-height: 28px;
vertical-align: top;
border: none;
border-radius: 0;
-webkit-appearance: none;
color: #bebebe;
background-color: #3c3c3c !important;
}
.list_search .in_s {
position: absolute;
top: 0;
right: 0;
border-left: 0px;
background: url(https://i.imgur.com/jKg1DRt.gif) left top no-repeat;
width: 52px;
height: 28px;
box-sizing: border-box;
padding-left: 52px;
}
/*우측하단_글쓰기, 검색버튼_제거*/
.paging_box .btn_list .btn_write {
display: none;
}
.paging_box .btn_list a {
display: none;
}
/*이전게시판_테이블_속성*/
.prev_list strong {
color: #bebebe;
background: #3c3c3c;
}
.prev_list a {
border: none;
color: #afafaf;
background: #3c3c3c;
}
.prev_list .previous {
color: #afafaf;
}
/*전체칸내리기*/
.index #container .contents {
overflow: hidden;
padding-bottom: 15px;
}
/*=================================================================================*/
/*본문글 상단속성*/
.left_cont .titles {
display: block;
border: 0px;
border-bottom: 0;
background: #3c3c3c;
padding: 20px 180px 0 20px;
color: #cdcdcd;
font-weight: bold;
font-size: 14px;
letter-spacing: -1px;
line-height: 150%;
}
/*본문글_닉네임_폰트*/
.view_head .items .text .nick {
color: #cdcdcd;
font-weight: bold;
margin-right: 10px;
}
/*본문글_말머리_폰트*/
.left_cont .titles .word {
color: #cdcdcd;
display: inline-block;
margin-right: 5px;
}
/*본문_이모티콘_속성*/
.view_head .items .photo a img {
border: none;
border-radius: 100%;
}
