css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('fogbugz.com'), url-prefix('https://fogbugz.com') {
#containerLogo {display: none;}
#navTop {visibility: hidden; display: none;}
#mainArea {padding: 0 !important}
#belowBanner {display: none}
#mainnav {padding-left: 16px}
#mainnav a.navlink {font-weight: normal !important}
#listName a, #listName a:link, #listName a:visited, #listTitle a, #listTitle a:link, a:visited {font-weight: normal !important; }
#listName {font-weight: normal !important;}
a.vb:link, a.vb:visited, body .favoritesPopup a.vb:link, body .favoritesPopup a.vb:visited { color: #222 !important;}
a.uvb:link, a.uvb:visited, body .favoritesPopup a.uvb:link, body .favoritesPopup a.uvb:visited{ color: #222 !important;}
div.bugShadowUR {background: none !important}
div.bugShadowBL {background: none !important}
div.bugShadowBR {background: none !important}
.buttonbar .nextprev {display: none !important}
.buttonbar {background: none !important}
a#email0 {display: none !important}
a#email1 {display: none !important}
a.icon {display: none !important}
div.bugShadowRight {background: none !important}
div.bugShadowBottom {background: none !important}
#bugviewContainer .top .row1 {padding-top: 10px}
#bugviewContainer {border: none !important}
#BugFields {margin-top: 0 !important}
#mainArea form {padding-top: 5px !important}
a:visited {color: #222 !important}
#fbsidebar {display: none}
#idActionP {display: none !important}
th.newIcon {display: none !important}
table.noBorder, span.noBorder, form.noBorder, div.noBorder, button.noBorder, img.noBorder, table.noBorder tr, table.noBorder td, table.noBorder th { background-color: white !important}
#listHeader {background-color: white !important}
.bugList .bugs th, .biglist th, .smalllist th, .mini-table th {background: white !important}
#mainArea .bugList .bugs td.rCB, #mainArea .bugList .bugs th.rCB, .biglist th.L, .biglist td.L {border: none !important}
.bugList .bugs th {border: none !important}
.buttonbar {width: 338px !important; padding-top: 0 !important; color: #ccc !important;}
#banner {border-top: 2px solid #E0E9F1 !important}
td#listNav img {display: none !important}
}
.fixfor {display: none !important}
div.buttons a {color: #222 !important}
img.catIcon {display: none !important}
#banner {background-color: white !important; border-bottom: 2px solid white !important}
#bugviewContainerSide  div.block div.rss { display: none !important}
#bugviewContainerSide  div.block div.outline { display: none !important}
#bugviewContainerSide  div.block #containerSubscribe { display: none !important}
#sidebar_priority_-1 { display: none !important}
a:link, a.novisited:visited, #mainArea a.novisited:visited {color: #000 !important}
* {font-weight: normal !important}
* * {font-weight: normal !important}
* * * {font-weight: normal !important}
* * * * {font-weight: normal !important}
#searchFor, #idDropList_searchFor_oText {border-width: 1px !important; border-color: #ccc !important; border-style: solid !important;}
.dlgText, textarea {border: none !important}
#BugFields {z-index: 5 !important;}
#bugviewContainerTop {min-height: 0 !important;}
#bugviewContainer .top .ixBug {font-size: 1em !important; height: auto !important; display: none !important; }
#btnNavbarSearch {display: none !important; border: none !important;}
#bugviewActionButtonsTop {z-index: 999 !important;margin-top: 0px !important; position: relative !important; padding-bottom: 15px !important;}
#bugviewContainer .top .title {font-size: 1.2em !important; padding: 0px !important; width: 530px !important;}
#statusbarspacer {display: none !important}
#bugviewContainer .top .statusbar {margin-left: -4px !important}
#bugviewContainer .top .idTitleProjectAndArea { min-height: 0 !important; margin-left: 0 !important; }
#bugviewContainer .top .statusbar .status {padding-right: 29px}
#bugviewActionButtonsBottom {width: 43% !important; position: fixed !important; bottom: 0px !important; padding-left: 40px !important;}
#bugviewActionButtonsBottom div a {background-color: #ddd !important; padding: 30px !important; margin-left: 2em;}
