css:
@-moz-document regexp('.*/secure/RapidBoard.jspa.*')
{
/*
  .ghx-extra-field[data-tooltip$="None"]
  {
    display: none !important;
  }
*/

/* make the card "color" cover the whole card */

  .ghx-issue .ghx-grabber
  {
    top: 2px;
    left: 2px;
    right: 2px;
    bottom: 2px;
    width: inherit;
    opacity: 0.2;
    z-index: 0;
  }

  .ghx-issue .ghx-extra-fields .ghx-extra-field[data-tooltip^="Labels:"] 
  {
    border: 3px solid #cc230a;
    padding: 0 6px;
    border-radius: 3px;
    font-size: 11px;
  }
/*
    min-width:1em !important;
    margin-right: .25em !important;
    margin-bottom: .25em !important;
 */

  .ghx-extra-field[data-tooltip^="Component/s:"]
  {
    background-color: #93c47d;
    color: #fff;
    border-left: 2px solid #93c47d;
    border-right: 2px solid #93c47d;
    border-top: 2px solid #93c47d;
    border-bottom: 2px solid #93c47d;
   /* border-radius: 4px; */
    margin-right: .4em !important;
  }

/*
 * Days in column
 */

  .ghx-issue
  {
    margin-bottom: 6px;
  }

  .ghx-issue:hover .ghx-days::before
  {
    opacity: 1;
  }

  .ghx-issue .ghx-extra-fields .ghx-extra-field[data-tooltip$="None"] 
  {
    display: none;
  }

  .ghx-days::before
  {
    content: attr(title);
    display: block;
    padding: 2px 6px 1px;
    background: #d00;
    color: #fff;
    font: 11px/1 arial, sans-serif;
    text-align: center;
    opacity: .5;
  }

.ghx-days[title="0 days in this column"]::before,
.ghx-days[title="1 day in this column"]::before,
.ghx-days[title="2 days in this column"]::before,
.ghx-days[title="3 days in this column"]::before,
.ghx-days[title="4 days in this column"]::before,
.ghx-days[title="5 days in this column"]::before
  {
    background: #999;
  }

  .ghx-days > b
  {
    display: none;
  }
} /* end @-moz-document domain(example.com) */
