css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.alc.co.jp/) {
 div#header, div#pagetop,
 div#gnav, div#body-tab1, div#body-tab2,
 div#side_nav, div#sub_left, div#sub_right,
 div#main,  div#footer, #copyright,
 #head-tab1, #head-tab2
 {
   display: none !important;
 }
}

@-moz-document url-prefix("http://eow.alc.co.jp/") {
 div#AreaHeaderCenter, div#AreaHeaderRight, div#AreaGnavi,
 div#AreaUpperRight, div#AreaSponsorSite, div#AreaFooter,
 div#gnav, ul#tabhead, div#AreaBody > div.bg_i
 {
   display: none !important;
 }
div#AreaHeaderLeft > a:before {
  content: url('http://eowimg.alc.co.jp/content/img/s_eijiro_r.jpg') !important;
}
div#AreaHeaderLeft img {
  display: none !important;
}
#AreaUpperLeft > table > tbody > tr:first-child,
#AreaUpperLeft > table > tbody > tr:first-child + tr > td:last-child,
#AreaUpperLeft > table > tbody > tr:first-child + tr + tr > td:last-child {
  display: none !important;
}

div#AreaBody {
  width: 800px !important;
  max-width: 95% !important;
  margin: 0px auto 0px 20px !important;
  padding: 10px 10px 5px 0px !important;
  border-right: 3px solid #afafaf !important;
}
div#AreaHeaderLeft, div#AreaUpperLeftContainer, div#AreaUpperLeftInner {
  width: 800px !important;
  max-width: 95% !important;
}

/* contents */
div#AreaUpperLeft {
  width: 800px !important;
  max-width: 100% !important;
}
div#AreaUpperLeft > table,
div#AreaUpperLeft > table > tbody > tr:first-child + tr + tr,
div#AreaUpperLeft > table > tbody > tr:first-child + tr + tr > td:first-child {
  width: 800px !important;
  max-width: 100% !important;
}

div[style~="dotted"] {
  margin-left: auto !important;
  margin-right: auto !important;
  margin-top: 10px !important;
  width: 640px !important;
}
div[style~="dotted"] > span {
  font-size: 10.5px !important;
}

table#naviArea {
  width: 800px !important;
  max-width: 100% !important;
}
div#resultArea {
  margin-top: 10px !important;
  font-size: 14px !important;
  line-height: 1.6 !important;
}
div#resultArea > table.mt_10 {
  width: 800px !important;
  max-width: 100% !important;
}
div#resultArea > table td[width="73"] {width: 0px !important;}
div#resultArea li {list-style: none !important;}
div#resultList > ul > li > div {margin-bottom: 15px !important;}
div#AreaLowerLeft {
  width: 800px !important;
  max-width: 100% !important;
}
}
