css:
@-moz-document domain("kachelmannwetter.com") {
     :root {
        color-scheme: dark;
    }
    .kw-site {
        background-color: hsl(0deg 25% 47%);
        background-color: hsl(0deg 25% 22%);
        padding-bottom: 315px;
        margin-top: -36px;
    }
    select.form-control {
        background-color: hsl(0deg 25% 31%);
        border: 2px solid hsl(0deg 25% 28%);
        color: #eee;
    }
    html,
    body {
        background-color: hsl(0deg 25% 22%);
    }
    body {
        color: rgba(255, 255, 255, 0.7);
    }
    .navbar-own,
    .navbar2-own {
        background-color: hsl(0deg 25% 47%);
    }
    #autorefresh-info {
        color: hsl(200deg 49% 23%);
    }
    @media (min-width: 1492px) {
        .kw-layout-wrapper {
            width: 1537px;
        }
    }
    @media (min-width: 1200px) {
        .kw-content-wrapper {
            width: 1600px;
        }
        #content-image {
            width: 1020px;
        }
    }
    .kw-ad-right {
        display: none;
    }
    div#drop-downs {
        color: #ffffffa6;
    }
    @media (min-width:1200px) {
        div#drop-downs {
            padding: 34px;
            background: hsl(0deg 25% 26%);
        }
    }
    .ac-btn,
    .ac-btn-disabled,
    .valid-btn,
    .mod-btn,
    .mod-btn-disabled {
        border: 1px solid #b8b5b54d;
        background-color: transparent;
        border-color: hsl(0deg 14% 39% / 57%);
        color: #d7d1d1ba;
    }
    .nav-tabs>li.active>a,
    .nav-tabs>li.active>a:hover,
    .nav-tabs>li.active>a:focus {
        background-color: hsl(0deg 25% 40%);
        color: hsl(0deg 0% 100%);
        border: 0px solid hsl(0deg 0% 87% / 50%);
    }
    a.ac-btn,
    a.valid-btn {
        color: hsl(0deg 0% 87%);
        background-color: hsl(0deg 25% 26%);
        border: 1px solid hsl(0deg 2% 59% / 30%);
    }
    a.btn-active {
        background-color: hsl(0deg 23% 31%);
        color: hsl(0deg 0% 100% / 64%);
        border: 0px solid hsl(0deg 0% 87% / 50%);
    }
    .dropdown-menu {
        background-color: hsl(0deg 25% 79%);
    }
    .acc2-btn:hover,
    .acc2-btn:focus,
    .acc2-btn:active {
        background-color: hsl(0deg 25% 20%);
        border-color: hsl(0deg 25% 41%) !important;
        color: #fff !important;
    }
    .acc2-btn {
        margin-bottom: 5px;
        margin-top: 5px;
        border-color: hsl(0deg 25% 31%) !important;
        border-width: 0px;
        background-color: hsl(0deg 25% 17%);
        color: #d6d6d6 !important;
    }
    .navbar2-fixed-top {
        border-top: 0px #007dfe solid;
        background-color: hsl(0deg 25% 26%);
        color: #fff;
        padding: 3px 0px;
        border-bottom: 0px solid hsl(0deg 25% 30%);
    }
    .myfavs {
        color: hsl(0deg 25% 79%);
    }
    .navbar2-brand-headline a,
    .navbar2-own a {
        color: #ffffffa6;
    }
    input#forecast-input-nav {
        background-color: #0003;
        color: #fff;
        height: 30px;
    }
    .ffnav-btn-geo,
    .ffnav-btn-search {
        background-color: hsl(210deg 100% 30%);
    }
    .navbar2-search .input-search {
        border: 0px solid #999;
    }
    .faq-button,
    .faq-button-search,
    .faq-button-search-mobil,
    .faq-button-search-mobil2 {
        color: hsl(85deg 24% 57%);
    }
    span.kw-play-button {
        background-color: hsl(85deg 92% 45% / 50%);
    }
    .acc-btn {
        background-color: hsl(0deg 25% 17%);
    }
    .input-group-addon2,
    .input-group-addon3,
    .input-group-addon {
        background-color: hsl(0deg 25% 17%);
        color: #d6d6d6;
    }
    .kw-slider .slider .slider-track .slider-selection,
    #animation-speed-container .slider .slider-track .slider-selection {
        background-color: hsl(68deg 51% 40%);
        border-radius: 0px;
    }
    .kw-slider .slider .slider-track,
    #animation-speed-container .slider .slider-track {
        background-color: #eeeeee7a;
    }
    .kw-slider .slider .slider-handle,
    #animation-speed-container .slider .slider-handle {
        background-color: #d6d6d6;
        box-shadow: none;
    }
    .btn-default {
        background-color: #d7bcbc;
        border-color: #965a5a;
    }
    .nav-tabs {
        border-bottom: 1px solid hsl(0deg 25% 66% / 53%);
        background: none;
    }
    .nav-tabs>li>a {
        color: hsl(0deg 22% 93%);
    }
    .player-range.kw-slider {
        margin: 0;
    }
    #content-image img {
        width: 1000px;
    }
    div#scale-overlay>img {
        filter: brightness(0.66);
    }
    #copyright_info {
        background-color: #808080;
        color: #000;
    }
    .overview-city>div>a {
        color: #ede7e7;
    }
    .overview-forecast {
        border-top-color: #d6d6d64a;
    }
    .csred {
        color: #d7413c;
    }
    .form-control {
        color: #ead9d9;
        background-color: hsl(0deg 25% 15%);
    }
    .input-search {
        border-color: #8d403e;
        border-color: hsl(68deg 51% 31%);
        border-color: hsl(68deg 70% 18%);
    }
    .select-search {
        border-color: hsl(0deg 25% 31%);
    }
    .messwerte-index h3>a {
        color: #cdc6c6;
    }
    .messwerte-index a {
        color: #b3c2d1;
    }
    .head-line {
        border-bottom: 3px solid hsl(68deg 51% 29%);
    }
    .panel {
        background-color: #382828;
    }
    .panel.panel-days {
        background-color: #0000005e;
        background-color: #382828;
        color: #ffffff8f;
    }
    .panel.panel-own {
        border-color: #ffffff00;
        background-color: #ffffff1a;
    }
    rect.highcharts-plot-background {
        fill: #462a2a;
    }
    .highcharts-container svg.highcharts-root g>rect[fill="#EEEEEE"] {
        fill: #0d0606;
    }
    svg.highcharts-root>g[data-z-index="0"]>rect[x="0"][fill="#dbecfe"] {
        fill: #2e1e1ecf;
    }
    g.highcharts-grid.highcharts-xaxis-grid path,
    g.highcharts-grid.highcharts-yaxis-grid path {
        stroke: #ffffff1f;
    }
    .highcharts-axis-labels.highcharts-xaxis-labels>span {
        color: #ffffffa8;
    }
    g.highcharts-axis-labels.highcharts-yaxis-labels text {
        fill: #ffffffa6;
    }
    g.highcharts-label text {
        fill: #ffffffa8;
    }
    .btn-grey {
        background-color: transparent;
        border-color: hsl(0deg 14% 39% / 30%);
    }
    .btn-spacer a.btn.btn-grey {
        color: #eeeeee87;
    }
    .nexthours-scroll::-webkit-scrollbar {
        height: 10px;
        background: #eeeeee59;
    }
    .nexthours-scroll::-webkit-scrollbar-thumb {
        background: #cccccc8a;
    }
    .wsymbol-morning,
    .wsymbol-afternoon,
    .wsymbol-evening {
        border-color: transparent;
    }
    .btn-spacer {
        margin-bottom: 6px;
    }
    span.model-legend {
        color: hsl(0deg 0% 63%);
    }
    .disclaimer-norway {
        font-size: 11px;
        color: #d6d6d6a6;
    }
    .lp {
        color: #000;
    }
    .faq-button-param {
        color: hsl(85deg 57% 31%);
    }
    span.klima-menue {
        letter-spacing: inherit;
        padding-left: 0;
        border-radius: 50%;
        margin-left: -17px;
    }
    span.klima-menue::before {
        display: inline-block;
        width: 13px;
        height: 13px;
        border: 3px solid #3674a8;
        border-radius: 50%;
        content: "";
        position: relative;
        left: 0px;
        top: 2px;
        margin-right: 4px;
    }
    ul#w10 {
        padding-left: 10px;
    }
    .modal-content {
        background-color: hsl(16deg 31% 60%);
        color: hsl(0deg 0% 0%);
        font-size: 18px;
    }
    .modal-footer {
        background-color: transparent;
        border-top: 0px solid hsl(0deg 25% 63%);
    }
    .modal h4 {
        font-size: 24px;
    }
    .modal-header {
        background-color: hsl(16deg 30% 48%);
        border-bottom: 1px solid hsl(0deg 25% 63%);
        color: #ffffffba;
        padding: 30px;
    }
    .modal-body {
        padding: 30px;
    }
    #temp_graph>div>svg>g[data-z-index="7"]>image {
        backdrop-filter: revert;
    }
    #rain_graph>div>svg>g.highcharts-series-group>g.highcharts-series.highcharts-series-0.highcharts-column-series.highcharts-tracker>rect.highcharts-point {
        /* fill: #098282; */
        stroke: lightgreen;
    }
    #pressure_graph>div>svg>g.highcharts-series-group>g.highcharts-series.highcharts-series-0.highcharts-line-series>path.highcharts-graph {
        stroke: #ffffff8a;
    }
    #rainsum_graph>div>svg>g.highcharts-series-group>g.highcharts-series.highcharts-series-0.highcharts-line-series>path.highcharts-graph {
        stroke: #509ada;
    }
    path.highcharts-scrollable-mask {
        fill: none;
    }
    .panel-body.nextdays {
        background: #2d1e1e;
    }
    .navbar-nav .open .dropdown-menu {
        background-color: #d7bcbcc7;
    }
    .highcharts-series-hover {
        stroke: #111;
        fill: yellow;
        color: green;
    }
    svg>g.highcharts-label.highcharts-tooltip.highcharts-color-0>text {
        color: #333333;
        fill: #333333;
    }
    /*
svg.highcharts-root > g[data-z-index="0"] > rect[x="0"] {
    fill: #af7c7c5e;
}
*/
    svg>g.highcharts-series-group>g.highcharts-series.highcharts-column-series.highcharts-tracker>rect.highcharts-point[stroke="#ffffff"] {
        stroke: none;
    }
    /* ECMWF color */
    rect.highcharts-point[fill="#000099"] {
        fill: #007dfe;
    }
    .wsymbol {
        filter: contrast(.5);
    }
    g.highcharts-series.highcharts-line-series>path[stroke="#000099"] {
        stroke: #007dfe;
    }
    .graph-headline {
        color: #ffffffba;
    }
    .graph-headline-compact {
        color: #ffffff9c;
    }
    .pay-btn {
        background-color: #007dfe;
    }
    #forecast-ensemble-parameters>li>ul>li>a.graphtab.pay-btn {
        color: #fff;
    }
    path.highcharts-plot-band {
        fill: #0b00002b;
    }
    rect[fill="#ccd9f2"] {
        fill: #172a3a;
    }
    path.highcharts-boxplot-whisker,
    path.highcharts-boxplot-stem,
    highcharts-boxplot-median {
        stroke: #8e8eb5;
    }
    rect.highcharts-point[stroke="#fde81a"] {
        fill: #fbfb0412;
        stroke: #fbfb0454;
    }
    .panel-own>.panel-heading,
    .panel-compact>.panel-heading {
        border-color: #632121;
    }
    path.highcharts-axis-line {
        stroke: black;
    }
}
