css:
@-moz-document domain("www.roblox.com"), domain("web.roblox.com"), domain("forum.roblox.com")
{
  #groups-switcher,.btn-control-md,.btn-control-xs,.btn-primary-md,.btn-secondary-md,.btn-secondary-xs,.dialog-container.dialog-visible,.dropdown-menu,.form-control,.form-group,.game-card-container,.game-card-native-ad,.game-card-thumb,.input-dropdown-btn,.item-card-container,.menu-vertical,.nav-menu-title,.popover,.popover-content,.profile-container .asset-item img,.profile-container .asset-item span[title],.scroller,.section-content,.stack-row,.vertical-menu,div.col-sm-6.text-overflow.slide-item-container-right.groups,img.header-thumb
  {
    border-radius: 14px!important;
  }

  .section-content-off
  {
    border-radius: 14px;
  }

  .btn-primary-lg
  {
    border-radius: 30px;
    padding: 12px;
  }

  div.col-sm-6.slide-item-container-left div a img
  {
    border-radius: 30px!important;
  }

  #chat-header,#chat-main,#dialog-container,.chat-windows-header,.dialog-container.group-dialog.ng-scope.dialog-visible
  {
    border-radius: 14px 14px 0 0!important;
  }

  .dialog-message
  {
    border-radius: 10px!important;
  }

  .small.dialog-message.dialog-message-content.dialog-triangle.ng-binding
  {
    box-shadow: none;
  }

  #navbar-search-input
  {
    border: none;
  }

  .nav-menu-title,.nav-menu-title:hover
  {
    background: rgba(0,0,0,0)!important;
  }

  #navbar-search-btn
  {
    visibility: hidden;
  }

  #CreateGroupBtn,.translate
  {
    border: none;
    border-radius: 10px;
    box-shadow: 0 0 4px 0 rgba(0,0,0,.2);
    background: #fcfcfc;
  }

  #CreateGroupBtn,#ctl00_cphRoblox_GroupSearchBar_SearchButton,.btn-control.btn-control-medium,.btn-control.btn-control-medium:hover
  {
    background-image: none;
    background-color: #00A2FF;
    color: #fff;
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    font-size: 14px;
    padding-left: 8px;
    padding-right: 8px;
    border: none;
    height: 25px!important;
  }

  #ctl00_cphRoblox_GroupSearchBar_SearchKeyword,#ctl00_cphRoblox_GroupStatusPane_StatusTextBox
  {
    padding: 10px;
    border: none;
  }

  .GroupControlsBox
  {
    border: none;
    background: #fff;
    padding: 8px;
    border-radius: 20px;
    box-shadow: 0 0 4px 0 rgba(0,0,0,.2);
  }

  #CreateGroupBtn
  {
    background-image: none!important;
    font-size: 18px!important;
    height: 35px!important;
    border-radius: 100px;
  }

  #navigation
  {
    transform: translate(0);
    transition: ease-out 70ms;
  }

  #horizontal-tabs,.page-content .rbx-tabs-horizontal .rbx-tab,.rbx-tab-heading
  {
    border-radius: 14px!important;
    box-shadow: none!important;
  }

  #horizontal-tabs
  {
    margin-top: 4px!important;
    margin-bottom: 4px!important;
    background: rgba(0,0,0,0);
  }

  .rbx-tab
  {
    width: 23%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }

  .rbx-tab .rbx-tab-heading:hover
  {
    background-color: #efefef;
    transition: ease-out .1s;
  }

  .rbx-tab .rbx-tab-heading
  {
    background-color: #fff;
    transition: ease-out .1s;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,.2)!important;
  }

  .rbx-tab .rbx-tab-heading:hover
  {
    box-shadow: 0 2px 6px 0 rgba(0,0,0,.25)!important;
  }

  #mCSB_1_container ul li
  {
    padding: 2px;
    transition: ease-out .2s;
  }

  #mCSB_1_container ul li:hover
  {
    transition: ease-out .2s;
    background-color: #efefef;
    border-radius: 8px;
  }

  #mCSB_1_container ul li a span,#mCSB_1_container ul li a span:hover
  {
    color: #000!important;
  }

  .rbx-tab.active .rbx-tab-heading,.rbx-tab.active .rbx-tab-heading:hover
  {
    color: #fff!important;
    background: #00A2FF!important;
    transition: ease-out .1s!important;
  }

  .rbx-tab.active .rbx-tab-heading
  {
    box-shadow: 0 2px 6px 0 rgba(0,0,0,.2)!important;
  }

  .dropdown-menu,.dropdown-menu li:hover,.menu-option.active,.popover-content
  {
    box-shadow: none!important;
  }

  .dropdown-menu li,.dropdown-menu li:hover
  {
    transition: ease-out .15s;
  }

  .enable-three-dee.btn-control.btn-control-small,button.input-addon-btn
  {
    border-radius: 100px!important;
    border: none!important;
  }

  .form-control.input-field.search-input.ng-pristine.ng-valid.ng-empty.ng-valid-maxlength.ng-touched
  {
    margin-right: 10px;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }

  footer.container-footer
  {
    border-radius: 40px 40px 0 0;
    width: 90%;
    margin-left: 5%;
    margin-right: 5%;
  }

  .home-header-content h1 a
  {
    font-weight: 300;
    font-size: 30px;
  }

  button.input-dropdown-btn.category-options.ng-scope
  {
    margin-right: 10px;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }
}

@-moz-document url("https://www.roblox.com/my/groups.aspx"), url-prefix("https://www.roblox.com/My/Groups.aspx?gid=")
{
  #Body,#rbx-body
  {
    background: #efefef;
  }

  #SearchControls
  {
    border: none;
    background: rgba(0,0,0,0);
  }

  .GroupListContainer.StandardBox
  {
    border: none!important;
    background: #fff!important;
    border-radius: 20px;
    padding: 6px;
  }

  #left-column
  {
    width: 140px;
    margin-right: 10px;
  }
}

@-moz-document url("https://www.roblox.com/my/avatar")
{
  .rbx-tab
  {
    width: 18%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }
}

@-moz-document url-prefix("https://www.roblox.com/my/messages/")
{
  ul.nav.nav-tabs
  {
    background: rgba(0,0,0,0)!important;
    box-shadow: none!important;
  }

  li#next.pager-next a,li#previous a,li.first a,li.last a
  {
    border-radius: 100px!important;
    border: none!important;
  }

  .btn-control-sm
  {
    border-radius: 14px!important;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
    padding: 7px 12px;
  }

  label#includePreviousMessage::before,label#messageCheckbox::before,label#userCheckbox::before
  {
    border-radius: 100px!important;
  }

  div.message-reply textarea
  {
    border-radius: 12px!important;
  }

  #roblox-reply-btn,div.roblox-sendMessage-action button,div.section-content-off.ng-binding
  {
    border-radius: 14px;
  }

  .messages .messageDivider
  {
    transition: ease-out .2s;
    margin-bottom: 4px;
    border-radius: 14px;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }

  .messages .messageDivider:hover
  {
    transition: ease-out .2s;
    box-shadow: 0 1px 6px 0 rgba(25,25,25,.5);
  }
}

@-moz-document url-prefix("https://www.roblox.com/users/")
{
  .rbx-tab
  {
    width: 48%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }
}

@-moz-document domain("www.roblox.com"), domain("robloxlabs.com"), domain("wiki.roblox.com"), domain("devforum.roblox.com")
{
  *
  {
    font-family: "Comic Sans MS" !important;
  }

  .logo-big
  {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 125px;
    height: 40px;
    padding-left: 125px;
  }

  #site-logo.logo-big
  {
    background: url("https://i.imgur.com/OmJxbzE.png") no-repeat;
    background-size: 125px;
  }
}

@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("http://web.roblox.com"), url-prefix("https://web.roblox.com")
{
  div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding,
	div.profile-avatar-mask,
	.rbx-header,
	.chat-main .chat-header,
	.btn-secondary-xs,
	a#upgrade-now-button,
	span.notification-blue,
	a.btn-primary-lg,
	a.btn-primary-md,
	div.chat-windows-header.dialog-header,
	button.btn-secondary-md,
	div.avatar-status.chat-friend-status.online,
	.btn-large.btn-primary,
	.tab.active
  {
    background: rgb(33, 175, 205) !important;
    border-color: rgb(33, 175, 205) !important;
  }

  .c-version .search-options .menu-link:hover,
	.c-version .search-options .menu-link.active
  {
    color: #42afcd;
  }

  .avatar-back
  {
    background-image: url("https://i.imgur.com/xS5MlDo.png");
  }

  .breadcrumb-container li.breadcrumb-filter
  {
    background-color: #42afcd;
  }

  .radio input[type="radio"]:checked+label::after
  {
    background-color: #42afcd;
  }

  .nav-pills>li.active>a,
	.nav-pills>li.active>a:hover,
	.nav-pills>li.active>a:focus
  {
    background-color: #42afcd;
  }

  div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding
  {
    color: #42afcd;
  }

  .tab.active
  {
    border-radius: 6px 6px 0px 0px;
    color: #42afcd;
  }

  .tab
  {
    background: #42afcd;
    border-radius: 6px 6px 0px 0px;
    border: 0px;
  }

  .alert-info
  {
    background-color: #42afcd;
  }

  li.rbx-navbar-search-option.selected
  {
    box-shadow: 4px 0 0 0 rgb(33, 175, 205) inset !important;
  }

  a:link,
	a:visited,
	a:active
  {
    color: #42afcd;
  }

  div#CreateGroupBtn.btn-large.btn-primary
  {
    border: 0px;
    border-radius: 10px;
    height: 35px;
  }

  input#ctl00_cphRoblox_GroupWallPane_NewPostButton.btn-control.btn-control-large.GroupWallPostBtn.translate
  {
    position: static;
  }

  input#ctl00_cphRoblox_GroupSearchBar_SearchButton.group-search-button.translate,
	input#ctl00_cphRoblox_GroupStatusPane_StatusSubmitButton.btn-control.btn-control-medium.translate
  {
    border: 0px;
    border-radius: 6px;
    color: #42afcd;
    padding: 12px;
    margin-left: 20px;
    line-height: 0px !important;
  }

  .btn-control.btn-control-medium.translate,
	.group-search-button.translate,
	.btn-control.btn-control-large
  {
    border: 0px;
    border-radius: 4px;
    color: #42afcd;
    background: rgb(33, 175, 205) !important;
  }

  button.btn-control-medium.btn-control-disabled
  {
    border: 0px;
    border-radius: 4px;
    color: rgb(100, 100, 100);
    padding: 2px;
    background: rgb(33, 175, 205) !important;
  }

  .btn-neutral,
	.btn-neutral:link,
	.btn-neutral:active,
	.btn-neutral:visited
  {
    border-color: #42afcd;
    background-color: #42afcd;
    border-radius: 4px;
    background-image: url(https://www.roblox.com/imges/Buttons/StyleGuide/bg-btn-blue.png);
  }

  .btn-negative,
	.btn-negative:link,
	.btn-negative:active,
	.btn-negative:visited
  {
    border-color: #42afcd;
    background-color: #42afcd;
    background-image: url(https://www.roblox.com/imags/Buttons/StyleGuide/bg-btn-gray.png);
    border-radius: 4px;
  }

  .content .SearchKeyword,
	input#ctl00_cphRoblox_GroupStatusPane_StatusTextBox.translate,
	textarea#ctl00_cphRoblox_GroupWallPane_NewPost.GroupWallPostText
  {
    border: 0px;
    height: 30px;
    background: #21AFCD;
    border-radius: 6px;
    padding-left: 10px;
  }

  div.section-content
  {
    border-radius: 6px;
  }

  textarea#ctl00_cphRoblox_GroupWallPane_NewPost.GroupWallPostText
  {
    background: #42afcd;
    margin-bottom: 10px;
    margin-right: 25px;
    width: 480px !important;
  }

  div#SearchControls
  {
    border: 0px;
    background: #42afcd;
    height: 35px;
    border-radius: 6px;
  }

  #right-column .StandardBox
  {
    border-radius: 3px;
    border: 1px solid #42afcd;
    background: #eee;
    padding: 5px;
  }

  .btn-primary,
	.btn-primary:link,
	.btn-primary:active,
	.btn-primary:visited
  {
    background-image: none !important;
  }

  div.GroupListContainer.StandardBox,
	div.GroupControlsBox
  {
    border: 0px solid #42afcd !important;
    border-radius: 6px;
  }

  div.GroupControlsBox
  {
    padding-bottom: 10px;
  }

  .pill-toggle input:checked+label
  {
    background-color: #42afcd;
  }

  .btn-secondary-sm
  {
    background-color: #42afcd;
    border-color: rgba(0, 162, 205, 0);
  }

  .btn-primary,
	.btn-primary:link,
	.btn-primary:active,
	.btn-primary:visited
  {
    background-color: #42afcd;
    border-color: rgba(0, 162, 205, 0);
    border: 0px solid #ffffff !important;
    border-radius: 6px;
  }

  .dialog-container .dialog-message-container .dialog-message
  {
    background-color: #42afcd;
    border: 1px solid #42afcd;
  }

  .dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:before, .dialog-container .dialog-message-container.message-cluster-master .dialog-triangle:after
  {
    border-color: transparent transparent transparent #42afcd;
  }

  .StatusView .top
  {
    border-radius: 3px;
    background: #42afcd;
    padding: 5px;
    width: 317px;
  }

  .slide-switcher.groups
  {
    background-color: #42afcd;
  }

  .StatusView .bottom
  {
    background: url(https://i.imgur.com/iBPxeaK.png) no-repeat;
    padding: 5px;
    padding-right: 0;
  }

  .btn-control.btn-control-medium
  {
    border: 0px;
    border-radius: 4px;
    color: #ffffff;
    background: rgb(33, 175, 205) !important;
  }

  .btn-control.btn-control-medium
  {
    height: 21px;
    line-height: 21px;
    font-size: 12px;
    background-image: url(https://www.roblox.com/images/StyleGuide/btn-control-medium-tile.png);
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
	.rbx-tabs-horizontal .rbx-tab-heading:hover
  {
    box-shadow: 0 -4px 0 0 rgb(33, 175, 205) inset;
  }

  .icon-default-bc,
	.icon-bc,
	.icon-tbc,
	.icon-obc
  {
    background-image: url(https://i.imgur.com/8DSlyrM.png);
  }

  div.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding::before
  {
    border-color: transparent transparent transparent rgb(33, 175, 205);
  }

  .pagerbtns
  {
    background: url(https://i.imgur.com/jgJgHUi.png) no-repeat;
  }

  .icon-game,
	.icon-default-friendstatus,
	.icon-studio,
	.icon-online
  {
    background-image: url(https://i.imgur.com/EhK0j4d.png);
  }

  .CarouselPager .arrow-prev, .CarouselPager .arrow-next
  {
    background: url(https://i.imgur.com/32YJjqc.png);
  }

  .CustomizeCharacterContainer .AssetName a,
	.AttireCategory a.AttireCategorySelector,
	.AttireCategory .AttireCategorySelector_Selected,
	#ctl00_ctl00_cphRoblox_cphMyRobloxContent_cmdInvalidateThumbnails,
	.FooterPager a,
	.para-overflow-toggle .toggle-para,
	.text-link,
	.vlist .list-item .list-body .list-content a,
	.text-link:link,
	.vlist .list-item .list-body .list-content a:link,
	.text-link:hover,
	.vlist .list-item .list-body .list-content a:hover,
	.text-link:visited,
	.vlist .list-item .list-body .list-content a:visited,
	.text-link:active,
	.vlist .list-item .list-body .list-content a:active,
	.text-name,
	.text-name:link,
	.text-name:hover,
	.text-name:visited,
	.text-name:active,
	a.text-link,
	.InventoryItemName,
	#left-column .StandardBox a,
	#mid-column .StandardBox a,
	#right-column .StandardBox a,
	tr.forum-post>.forum-content-background:first-child .normalTextSmallBold
  {
    color: #42afcd;
  }

  .rbx-tab-horizontal-submenu li.active
  {
    background-color: #42afcd;
  }

  .rbx-tab-horizontal-submenu li:hover
  {
    background-color: #42afcd;
  }

  .menu-vertical .menu-option:hover, .menu-vertical .menu-option:focus, .menu-vertical .menu-option.active .menu-secondary-option.active
  {
    box-shadow: 4px 0 0 0 #42afcd inset;
  }

  .rbx-left-col:before
  {
    position: absolute;
    color: #00A9E0;
    font-size: 0pt;
    font-family: Arial, Helvetica, comic sans;
    content: "Theme created by Kevin";
    top: 0px;
    left: 0px;
    z-index: 1050;
  }
}
