css:
@-moz-document url-prefix("https://learnku.com") {
	.article-content :not(pre) > code[class*=language-],
	.article-content pre[class*=language-],
	.editor-preview pre,
	.editor-preview-active-side pre,
	.markdown-body pre {
		width: 108.8%;
	}
	code[class*=language-],
	pre[class*=language-] {
		width: 256%;
		height: auto;
	}
	.comment code[class*=language-],
	pre[class*=language-] {
		width: 100%;
	}
}
