css:
@-moz-document domain("nexusmods.com")
{
#head.new-head
{
    backdrop-filter: blur(30px);
    background-color: #00000073;
}
.agroup {
    visibility: hidden;
    height: 0px;
}
body .home-intro

{
    backdrop-filter: blur(30px);
    background: #00000017 !important;
}
.collections-banner-outer-wrapper-trigger .collections-banner-wrapper
{
    backdrop-filter: blur(30px);
    background: #00000017 !important;
}
.static .wrap {
    backdrop-filter: blur(30px);
    background: #00000017 !important;
}
ul.modtabs li
{
    background: #00000000;
}
ul.modtabs li a.selected {
    border-right: 1px solid #4c4c4c00;
    background: #00000069;
}
.static .container {
    background: #101010 !important;
}
.mod-tile-left {
    background: #000000;
}
.tiles .fadeoff {
    background: linear-gradient(to bottom,rgba(56,56,56,0) 0,rgb(0 0 0) 100%);
}
.tiles .tile-data {
    background: #00000000;
}
.tabcontent {
    background: #101010;
}
.video-ad {
background-color: #4440;
}
#rj-footer {
    backdrop-filter: blur(30px);
    background: #00000000 !important;
}
section#rj-vortex {
    background-color: #2220;
}
ul.modtabs {
    background: #2d2d2d00;
}
.static .container {
    backdrop-filter: blur(30px) !important;
    background: #00000026 !important;
}
.tabcontent {
    background: #10101000;
}
.article-page {
    background: #00000000;
}
.comments>ol>li {
    background: #00000047;
}
.comments .comment-head {
    background: #4f4f4f57;
}
.comments>ol>li ol>li {
    background: #ffffff0c;
}
.comments>ol>li ol>li .comment-head {
    background: #ffffff29;
}
.article-nav .article-label {
    color: #ffffff;
}
.article-nav ul li a .icon-arrow-thin {
    fill: #ffffff;
}
.article-actions {
    background: #24232266;
    border-bottom: 0.5px solid #a6a6a6;
}
.rj-user-nav a, .rj-login-nav a {
    border-radius: 2em;
}
.home-buttons .btn:last-of-type {
    border-radius: 2em;
}
body.new-head .btn-more .btn {
    border-radius: 2em;
}
.premium-block--register .premium-block__link-wrapper > a.rj-btn {
    border-radius: 2em;
}
body.new-head #games-list li.image-tile .mod-tile-left img.game_cover {
    border-radius: 0.5em;
}
ul.tiles li.article-tile .single-image a.post-category {
    border-radius: 2em;
}
.rj-vortex-button {
    border-radius: 2em;
}
#rj-sitemap ul li a.rj-standard-button {
    border-radius: 2em;
}
.inline-flex, a.btn.inline-flex, .btn.inline-flex {
    border-radius: 2em;
}
.modpage .info-details .sideitem a.btn:last-of-type, .modpage .info-details .sideitem a.btn:only-of-type {
    border-radius: 2em;
}
.motm_position, ul.friend-actions .btn, .widget-download-links .btn, .popup-register-copy .btn, .btn.signin, .btn-more .btn, .home-buttons .btn, .btn-load, .form-section-content .btn, ul.file-actions .btn, .add-clone, .form-section .btn.btnsmall, .add-nav .btn, .modactions .btn, .mobile-close, ul.actions .btn, #foot #backtop .btn, #usernav ul li a, #footbtn .btn, form.search input.btn, ul.search-terms li:after, .accordion dt .acc-status, .fake-accordion .acc-status, .acc-status, .tiles .btnexpand ul li, ul.btnexpand:hover, ul.btnexpand:focus, .popup .mfp-close, .tiles .view-label, .tracking-highlight span.update-bar, .edit, .crop, .set-primary, ul.add-process li.current:before, .select2-container--default .select2-selection--multiple .select2-selection__choice__remove, .info.callout, #mobilebtn, nav#mobilenav #mobile-user ul li a, ul#mobile-primary li a, .rvs-container .rvs-nav-item.rvs-active, .irs-bar, #head .btn, a.btn, .btn, .btn:visited, .toggle-button, .toggle-button:active, .toggle-button:visited, .rj-button-outline:hover, a.rj-button-outline:hover, .arrow-box, a.post-category.site-news, .select2-container--default .select2-results__option--highlighted[aria-selected] {
    border-radius: 2em;
}
}
