@-moz-document domain("chat.openai.com")
{
.md\:w-\[260px\] {
    width: 137px !important;
    background: linear-gradient(45deg, #000000, #11111100);
    backdrop-filter: blur(30px);
    padding: 0px !important;
}
.duration-200, .dark .btn-neutral, .pr-14 {
    border-radius: 3em;
    transition-property: all;
    transition-duration: .5s;
    border: none;
    background: #ffffff0a;
}
.duration-200:hover, .dark .btn-neutral:hover {
    box-shadow: inset 0 0 60px #ffffff59, inset 20px 0 80px #10a37f00, inset -20px 0 80px #00ffff47, inset 20px 0 300px #10a37f, inset -20px 0 300px #13bd94, 0 0 50px #fff, -10px 0 80px #10a37f, 10px 0 80px #0ff6;
    scale: 1.1;
}
.xl\:max-w-3xl
{
    max-width: 90rem;
}
.dark .dark\:bg-\[\#444654\] {
    background: linear-gradient(135deg, #10a37f08, #000000);
    border-left: solid 3px #10a37f !important;
    box-shadow: inset 30px 0px 30px -30px #10a37f !important;
}
.dark .btn-neutral {
    background-color: rgb(52, 53, 65, 0.11);
    border-radius: 2em;
    backdrop-filter: blur(10px);
border-color: rgb(86, 88, 105, 0.28);
}
.dark .dark\:shadow-\[0_0_15px_rgba\(0\2c 0\2c 0\2c 0\.10\)\] {
    background: transparent;
    backdrop-filter: blur(10px);
    border-radius: 2em;
border-color: rgb(86, 88, 105, 0.28);
}
.dark .dark\:text-gray-200 {
    backdrop-filter: blur(5px);
}
.dark .dark\:text-gray-100 {
    border-left: solid 3px #7d8b8d;
    box-shadow: inset 30px 0px 30px -30px #7d8b8d;
}
.dark .dark\:bg-vert-dark-gradient {
    background-image: linear-gradient(180deg,rgba(53,55,64,0),#000000ad 58.85%);
}
.md\:pl-\[260px\] {
    padding-left: 135px;
}
.rounded-sm {
    border-radius: 2em;
}
.dark .dark\:bg-gray-800 {
background: linear-gradient(135deg, #7d8b8d14, #000000);
}
.bg-orange-500\/80
{
background: linear-gradient(136deg, #e06c2b, #a33310);
}
.dark .dark\:bg-gray-800\/75 {
    background: #00000033 !important;
    backdrop-filter: saturate(1.5);
  }
.text-gray-800.w-full.md\:max-w-2xl.lg\:max-w-3xl.md\:h-full.md\:flex.md\:flex-col.px-6.dark\:text-gray-100 {
    border: transparent;
    box-shadow: 0px 0px transparent;
}
.flex.flex-col.items-center.text-sm.dark\:bg-gray-800 {
    background: transparent;
}
.dark .dark\:bg-gray-900 {
    background: transparent;
    backdrop-filter: blur(20px);
}
.gap-2
{
    border-radius: 2em;
}
.dark .dark\:text-gray-400
{
background: radial-gradient(black, transparent);
}
.text-gray-200 {
    background: linear-gradient(135deg, #7d8b8d87, #7e888c00);
}
.p-2
{
    width: 100%;
}
.from-gray-800
{
    visibility: hidden;
}
.lg\:px-0 {
    padding-left: 16px;
    padding-right: 16px;
}
.lnXdpd
{
filter: drop-shadow(0px 0px 18px black);
}
.from-gray-900
{
visibility: hidden;
}
.hover\:bg-\[\#2A2B32\]:hover
{
    border-radius: 2em;
    background: #ffffff0a;
}
.dark .dark\:md\:bg-vert-dark-gradient
{
    background: linear-gradient(180deg, #7d8b8d00, #131313) !important;
}
.react-scroll-to-bottom--css-ckmzr-1n7m0yu::-webkit-scrollbar-thumb {
    background: linear-gradient(135deg, #ffffff7a, #ffffff2e);
}
.dark .dark\:bg-\[\#444654\], .duration-200, .dark .btn-neutral
{
    filter: hue-rotate(/*[[GPTC]]*/deg);
}
#__next > div > div.flex.h-full.flex-1.flex-col.md\:pl-\[260px\] > main > div.flex-1.overflow-hidden > div > div {
    padding: 0px !important;
}
}
@-moz-document domain('platform.openai.com')
{
    /* Panel */
.pg-header, .pg-root .editor-container, .pg-footer-left, .pg-token-counter, .pg-right .parameter-panel, .fill-input textarea, .fill-result-container .fill-result, .transform-pg-body textarea, .transform-result-container, .transform-use-as-input, .modal-dialog, .ovr-page.page-body, .ovr-footer, .docs-body .markdown-page, .side-nav.side-nav-padded, .page-body.examples-page, .acct-subpage, .text-input-with-header .text-input-header-wrapper, .chat-pg-panel-wrapper
{
    background-color: #0000004f;
    backdrop-filter: blur(80px);
    border: 1px solid #ffffff21;
/*[[FROST]]*/
}

/* Buttons */
.css-1u8ecqi-control, .switch-field input:checked+label, .css-2krsg1-control, .inject-text-ta, .css-i4h7a8-control, .text-input, .markdown-content.markdown-prompt-blockquote blockquote, .example-details .modal-dialog .prompt, .example-details .modal-dialog .resp, .chat-pg-message.active .text-input-with-focus .text-input
{
    background-color: rgb(255, 255, 255, 0.19);
    border: 1px solid var(--gray-300);
    border-radius: 1em;
}

/* Text */
h1, h2, h3, h4, h5, h6, .css-2zv882-placeholder, .btn-neutral.btn-minimal, .feedback-btn:not(.selected):not(:hover), .pg-token-counter, .btn-node>svg, .switch-field label, .switch-field input:checked+label, .css-h2qa38-singleValue, .control-label, .css-bjbdno, .inject-text-ta, .fill-input textarea, .fill-result-container .fill-result, .transform-pg-body textarea, .transform-result-container, .mb-2, .transform-use-as-input, p, .modal-header, .text-input, .pg-right-panel-mobile-close, .icon-item-title, .ovr-footer-icon, .ovr-footer-title.body-large.bold.mt-2, a.side-nav-item:visited:not(.active), a.side-nav-item, .examples-page .text-title, .modal-root.modal-is-open .modal-dialog, .example-details .modal-dialog .details-title, .example-details .modal-dialog .left-header .tag, .example-details .modal-dialog .prompt, .example-details .modal-dialog .response-text, .usage-group-label, .usage-intro, .usage-chart-header .month-display .month-title, .chat-pg-exchange-container .add-message .text
{
    color: #ffffff;
}


.pg-root .editor-container, .pg-footer-left, .pg-token-counter.visible, .pg-right .parameter-panel, .fill-input textarea, .fill-result-container .fill-result, .transform-pg-body textarea, .transform-result-container, .transform-use-as-input, .modal-dialog, .ovr-page.page-body, .docs-body, .page-body.examples-page, .acct-subpage, onse-text, .chat-pg-panel-wrapper
{
    border-radius: 1em;
}
.icon-item-desc, .ovr-footer-desc, .examples-page .text-tagline, .example-details .modal-dialog .parameter-label, .app-header .menu-container .nav-item:visited, .app-header .menu-container .nav-item, .app-header .user-details .user-details-org {
    color: #c4c4c4;
}
.example-details .modal-dialog .parameter-value {
    color: #a4a4a4;
}
div#root {
    background: black;
}
.apexcharts-canvas {
    background: white;
    border-radius: 1em;
    margin-top: 10px;
}
.pg-header
{
    margin-left: -1px;
    border-right: none;
    transition: all 0.1s;
}
.usage-page
{
    color: #989898;
}
.example-details .modal-dialog .prompt, .example-details .modal-dialog .response-text {
    background-color: #ffffff17 !important;
}
.ovr-footer {
    background-color: var(--gray-50);
    background: transparent;
    margin-top: 20px;
    border-radius: 4em 4em 0px 0px;
}

.pg-header
{
backdrop-filter: none;
background: black;
}
.app-wrapper-max-height .app-wrapper, .page-wrapper {
background-image: url("/*[[BGRND]]*/");
    background-size: cover;
    background-position-x: center;
}
.btn-neutral.btn-filled, .DraftEditor-editorContainer, .switch-field, .css-160w4cx .text-input:not(:focus) {
    background-color: #ececf100;
    color: #ffffff;
}
:root
{
    --gray-300: #ffffff1a;
    --green-100: #ffffff21;
--gray-900: #949494;
}
.pg-root .completions-container
{
    border: transparent;
}
.btn-primary.btn-filled {
    background-color: #ffffff;
    border-radius: 2em;
    color: black;
}
.pg-right .parameter-panel {
    padding: 0 24px 24px;
    padding: 12px;
    margin-left: 24px;
    margin-right: 24px;
}
.css-160w4cx .rc-slider-rail {
    background-color: #ececf129;
}
.pg-right-content
{
    width: 100%;
    background: transparent;
}
.cb-input.cb-checked:not([disabled])+.cb-checkbox,.fill-result-container {
    background-color: #10a37f00;
    border-color: #10a37f00;
}
.syntax-highlighter.dark {
    --sh-bg: #000000;
}
.btn-node>svg
{
    color: #ffffff !important;
}
.pg-alert.note {
    border-radius: 1em;
    margin-top: 10px;
}
.transform-use-as-input {
    height: 25px;
    width: 105px;
    align-items: center;
}
.css-g8vx3q {
    color: #dcdcdc;
}
.btn-neutral.btn-filled:hover {
    background: #ffffff29 !important;
}
.btn-neutral.btn-filled
{
border-radius: 2em;
}
.app-header {
    background: black;
    border-bottom: 1px solid #000000;
}
.app-header .menu-container .nav-item.active {
    color: #ffffff;
    font-weight: 1000;
}
path#logo {
    fill: white;
}
.subheading {
    color: #ffffff;
    z-index: 10;
}
textarea.text-input.text-input-lg.text-input-full.text-input-header-buffer {
    background: transparent;
}
.chat-pg-exchange-container .chat-pg-message {
    border-bottom: 1px solid #ffffff21;
background: transparent !important;
}
.chat-pg-exchange-container .add-message .icon {
    fill: white;
}
.chat-pg-message .chat-message-role .subheading .chat-message-role-text {
    background: transparent !important;
}
.chat-pg-message .chat-message-button-container .chat-message-remove-button {
    fill: white;
}
}
