css:
@-moz-document domain("tracle.tv") {
  body {
    font-family: Albert Sans;
  }

  .btn-dark {
    color: #fff;
    background-color: #454545;
    border-color: none;
    background-image: none;
    box-shadow: none;
    text-shadow: none;
    outline: 0;
  }

  .btn-blue {
    color: #fff;
    background-color: #6891e7;
    background-image: none;
    box-shadow: none;
    text-shadow: none;
    outline: 0;
  }
  
  .btn-blue:hover {
    background-image: none;
    box-shadow: none;
  }

  .btn-gray {
    color: #555;
    text-shadow: none;
    background-color: #ffffff;
    box-shadow: none;
    border-color: none;
    outline: 0;
  }
  .video-js *, .video-js *:before, .video-js *:after {
    box-sizing: inherit;
    font-family: Albert Sans;
  }
}
