css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mozillaca.com") {
 html{
  min-height:100%;
 }

 body{
  min-height:100%;
  background:-moz-linear-gradient(top, #9b9, #057 400px);
 }

 #notice_data-text{
  font-family: Calibri, Tahoma, Verdana, Arial, sans-serif;
  background:-moz-linear-gradient(top, #ddd, white 20px);
  border-width: 2px !important;
  border-color: #888 !important;
  height:96px !important;
  -moz-border-radius:12px !important;
  -moz-box-shadow:0px 0px 3px rgba(0,0,0,0.1);
  -moz-transition: all 0.15s linear;
  overflow-x:hidden !important;
  padding:5px !important;
  padding-bottom:30px !important;
 }

 #notice_data-text:focus{
  background:-moz-linear-gradient(top, #ccc, white 20px);
  text-shadow:0px 0px 5px #fff;
 }

 #notice_data-text:hover{
  -moz-box-shadow:0px 0px 15px #fff;
 }

 #form_notice .form_note{
  right:80px !important;
  top:125px !important;
 }

 #wrap{
  margin-top:20px !important;
 }

 #site_nav_global_primary{
  display:block;
  width:100% !important;
  z-index:65535;
  position:fixed;
  text-align:right;
  top:0px;
  right:0px;
  background:#fdfeff;
  padding-right:10px !important;
  margin:0px !important;
  border-bottom:2px solid #555;
  -moz-transition: all 0.4s linear;
  opacity:0.8;
 }

 #site_nav_global_primary:hover{
  opacity:1.00;
 }

 #site_nav_global_primary a{
  color:#33a !important;
  font-weight:normal !important;
  text-decoration:none !important;
 }

 #site_nav_global_primary a:hover{
  text-decoration:underline !important;
 }

 #site_notice a{
  display:block;
  position:absolute;
  top:8px;
  right:10px;
  color:#fff !important;
  font-weight:bold;
 }

 #site_notice a::before{
  content: "Get Addons";
 }

 #site_notice a img{
  display:none !important;
 }

 #notice_data-attach{
  cursor:default !important;
 }

 label[for="notice_data-attach"]{
  cursor:default !important;
  background-color:#fff !important;
  -moz-border-radius:2px;
  border:2px solid #fff;
  opacity:0.65;
 }

 label[for="notice_data-attach"]:hover *{
  opacity:1;
 }
 
 #notice_action-submit,
 .form_group_join .submit,
 .form_user_subscribe .submit,
 #form_login .submit,
 .form_settings .submit
 {
  background:-moz-linear-gradient(top,
   rgba(255,255,255,1) 0%,
   rgba(0,0,0,0) 100%
  );
  border:1px solid #ccc !important;
  -moz-border-radius:6px !important;
  -moz-box-shadow:0px 0px 3px rgba(0,0,0,0.1);
  text-shadow:0px 0px 4px #000;
  padding:5px !important;
  width:auto !important;
  height:auto !important;
  -moz-transition: all 0.15s linear;
  margin-bottom:2px !important;
 }

 #notice_action-submit:hover,
 .form_group_join .submit:hover,
 .form_user_subscribe .submit:hover,
 #form_login .submit:hover,
 .form_settings .submit:hover
 {
  -moz-box-shadow:0px 0px 8px #fff;
  text-decoration:underline;
 }

 .logo{
  -moz-transition: all 0.2s ease;
  opacity:1;
 }

 .logo:hover{
  opacity:0.9;
 }
 
 #form_notice{
  width:70%;
 }

 #notices_primary .hentry.notice:hover{
  background:-moz-linear-gradient(top,
   rgba(255,255,255,1) 0%,
   rgba(0,0,0,0) 100%
  );
 }

 #aside_primary{
  border:0px solid #fff !important;
  background:-moz-linear-gradient(top,
   rgba(0,0,0,0.2),
   rgba(128,128,128,0) 30px,
   rgba(0,0,0,0) 300px
  );
 }

 #content{
  border:0px solid #fff !important;
  background:-moz-linear-gradient(top,
   rgba(0,0,0,0.2),
   rgba(128,128,128,0) 30px,
   rgba(0,0,0,0) 300px
  );
 }
 
 #core .nav a{
  background:-moz-linear-gradient(top,
   rgba(255,255,255,.2) 0%,
   rgba(0,0,0,.4) 100%
  );
 }

 #core .nav .current a{
  background:-moz-linear-gradient(top,
   rgba(255,255,255,1) 0%,
   rgba(0,0,0,0.2) 100%
  );
 }
}
