css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flashback.org") {



.tcat
{
	background:#b00723 !important;
	color: #ffffff !important;
}
.tcat a:link
{
        color: #ffffff !important;
}
.tcat a:visited
{
        color: #ffffff !important;
}
.tcat a:hover, .tcat a:active
{
        color: #ffffff !important;
}

.thead, .thead a:link, .thead a:visited, .thead a:hover, .thead a:active
{
	background: #b00723 !important;
	color: #ffffff !important;
}

table#threadslist .thead,
table.list .thead
{
	background:#b00723 !important;
}


div#top-menu {
	border-bottom: 3px solid rgb(12,2,1) !important;
	-webkit-box-shadow: 0 3px 6px rgba(12,2,1,0.67) !important;
	padding-top: 2px !important;
}
.banner_text {
	visibility: hidden !important;
	width: 0 !important;
	height: 0 !important;

}
div#top-banner,
div.fb_adsys_wrapper_TextAds,
div#site-right > a,
div#site-right > div.break {
	display: none !important;
}

div#site-right > div.box-right:first-child {
	display: block !important;
}

div#top-menu > ul > li > a,
ul#top-tabs > li > a,
#user-box,
.list-forum-title td a,
.tcat,
table#threadslist .thead a,
table#threadslist .thead,
table.list .thead a,
table.list .thead,
#top-menu ul.top-menu-main li a,
#top-right-online,
.box-right p,
.box-right table h2, .title,
.vbmenu_control,
a.doaction,
.smallfont2,
.georgia {
	font-family: Verdana !important;
}

}
