css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.flashback.org")
{


body
	{
	  background: url('http://i50.tinypic.com/2ldao0z.png') repeat-y #545454 !important;
	}


#site
	{
	  background-color: #F0F0F0 !important;
	}


#top-banner
	{
	  display: none !important;
	}


#top
	{
	  background: url('http://img716.imageshack.us/img716/7690/logows.png') !important;

	  margin-top: 10px !important;
	}


ul#top-tabs
	{
	  background-color: #FFFFFF !important;

	  margin: 5px 0 20px 0 !important;
	}


#site-right
	{
	  background-color: #E3E3E3 !important;
	  margin-top: 39px !important;

	  border: 1px solid #B2B2B2 !important;
	}


.box-right
	{
	  background-color: #F7F7F7 !important;

	  margin-bottom: 15px !important;
	}


.break
	{
	  display: none !important;
	}


.fl .icon-post-edit
	{
	  margin-left: 10px !important;
	}


.fl .icon-post-report
	{
	  margin-left: 10px !important;
	}


#posts tr td.head
	{
	  background-color: white !important;
	}


.fb_adsys_wrapper_TextAds
	{
	  display: none !important;
	}


table.tborder tr td
	{
	  background-color: #F7F7F7 !important;
	}


}
