css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flashback.org") {

#site {
background-color: #ffffff !important;
}

#site-left, .thread-pagenav, table, thead, tbody, tr, td {
background-color: #ffffff !important;
}

.list-forum-title {
border: 1px solid #B8C0C7 !important;
}

.list-forum-title .lft-color {
padding-left: 10px !important;
}

.list-forum-title tbody tr td {
background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAAZCAIAAABIPBwcAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAFNJREFUeNrs00ENACAMBEEg+DdYE1XQYqFvMidhsrczc9lst7spDHcQKAsWLFifYlUVBWXBckNlwXJDCsqC5YbKguWGFJQFCxasH7EigsJwT4ABANLuR3DCn/qIAAAAAElFTkSuQmCC) !important;
}

.forum-navbar {
border: 1px solid #B8C0C7 !important;
margin-top: 4px !important;
}

#posts {
border-left: 1px solid #B8C0C7 !important;
border-bottom: 1px solid #B8C0C7 !important;
border-right: 1px solid #B8C0C7 !important;
}

#posts > div {
padding: 0px !important;
}

.alignc .alt2 {
background: #f8f8f8 !important;
}

.alignc .alt2 > table > tbody > tr > td {
background: #f8f8f8 !important;
}

.alignc .alt1 {
background: #fbfbfb !important;
}

#site-right {
background-color: #ffffff !important;
padding-top: 3px !important;
}

#site-right div {
margin-bottom: 5px !important;
}

.alt1Active {
width: 65% !important;
}

/*.thread-pagenav-lastpage {
background: url(data:image/png;base64,)  no-repeat left bottom!important;
} */

.thread-pagenav-lastpage img {
visibility: hidden !important;
}

div#top-banner,
div.fb_adsys_wrapper_TextAds,
div#site-right > a,
div#site-right > div.break {
	display: none !important;
}

div#site-right > div.box-right:first-child {
	display: block !important;
}

.thead {
background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAAZCAIAAABIPBwcAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAFNJREFUeNrs00ENACAMBEEg+DdYE1XQYqFvMidhsrczc9lst7spDHcQKAsWLFifYlUVBWXBckNlwXJDCsqC5YbKguWGFJQFCxasH7EigsJwT4ABANLuR3DCn/qIAAAAAElFTkSuQmCC) !important;
border-top: 1px solid #B8C0C7 !important;
border-bottom: 1px solid #B8C0C7 !important;
}

.clear .signature, .clear.signature a {
color: #888888 !important;
}

.icon-post-edit {
background: #ffffff url(data:image/png;base64,) !important;
}

.icon-post-report {
background: #ffffff url(data:image/png;base64,) !important;
}

.icon-post-quote {
background: #ffffff url(data:image/png;base64,) !important;
}

.icon-post-reply-small {
background: #ffffff url(data:image/png;base64,) !important;
}

.icon-post-forward {
background: #ffffff url(data:image/png;base64,) !important;
}

.icon-sticky {
background-color: #ffffff !important;
}

.imagebutton {
background: #ffffff !important;
}

.panel {
background: #ffffff !important;
}

.vBulletin_editor {
background: #ffffff !important;
}

#site-right > .box-right:first-child .georgia {
color: #000000 !important;
font-style: normal !important;
}

#collapseobj_threadreview {
margin-top: 5px !important;
}

#collapseobj_threadreview .thead {
border-left: 1px solid #B8C0C7 !important;
border-right: 1px solid #B8C0C7 !important;
}

#collapseobj_threadreview .alt2.bold {
background: #f8f8f8 !important;
}

#collapseobj_threadreview .alt1 {
background: #fbfbfb !important;
}

form[action="private.php"] #post .thead {
border-top: 0px !important;
}

form[action="private.php"] #post {
border: 1px solid #B8C0C7 !important;
}

.vbmenu_hilite:active, .vbmenu_hilite:hover, .vbmenu_option:hover {
background: #c0c0c0 !important;
}

#threadrating_menu .vbmenu_option:hover {
background: #ffffff !important;
}

img[src="https://static.flashback.info/img/b/738.gif"], img[src="https://static.flashback.info/img/b/738.gif"] + br, img[src="https://static.flashback.info/img/b/738.gif"] + * + br {
display: none !important
}

.pagenav.nborder.fr span.smallfont2 strong {
text-decoration: underline !important;
}

code *, pre * {
font-family: tahoma, monospace, sans-serif !important;
font-size: 11px !important;
line-height: 1.4em;
}

.post-bbcode-php, pre {
width: 95% !important;
}

.icon-post-quotem {
background: url(data:image/png;base64,) !important;
}

.vbmenu_hilite a[onclick="return snc.set_text(0)"] {
color: #19b010 !important;
}

TD[class="alt1"][style="background: none repeat scroll 0% 0% rgb(221, 221, 221);"] {
background: #fbfbfb url(data:image/png;base64,) no-repeat !important;
background-position: right bottom !important;
}

.icon-user-offline {
background: url(data:image/png;base64,) no-repeat !important;
width: 40px !important;
height: 12px !important;
}

.icon-user-online {
background: url(data:image/png;base64,) no-repeat !important;
width: 40px !important;
height: 12px !important;
}

.icon-user-invisible {
background: url(data:image/png;base64,) no-repeat !important;
width: 40px !important;
height: 12px !important;
}

#top-tabs-icons td[style="padding: 0px 4px;"] {
font-weight: bold !important;
color: #f00 !important;
background: #fafafa !important;
}

ul#top-tabs {
width: 99.3% !important;
}

#text-input {
height: 24px !important;
overflow:hidden !important;
}

#text-input input#message {
height: 24px !important;
font-size: 13px !important;
margin-bottom: 5px !important;
padding-top: 0px !important;
padding-bottom: 0px !important;
padding-left: 5px !important;
}

#toolbar tr, #toolbar td {
background: #F0F0F0 !important;
}

}
