css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.toutatice.fr") {
/* body et wrapper*/
td{
color:white;
}

table tbody tr td {
background-color:black;
}

body {
color:#ffffff;
background-color:#000000;
font-family: "Comic Sans MS",serif;
}



p {
font-family: "Comic Sans Ms",serif;
}

tr td img {
display:none;
}

#wrap {
background-color:#000000;
color:#303030;
margin:0px auto;
padding:0px;
width:984px;
margin-left:0px;
}

/* header */

#header {
color:#000000;
background:#ffffff bottom right no-repeat;
height:80px;
margin:0px;
padding:0px;
text-align:right;
}

.soustitre {
font-family: "Comic Sans MS",serif;
color:white;
background-color:black;
height: 30px;
}

#content {
border:0px solid red;
margin:0px 10px 10px 200px;
padding:0px;
text-align:right;
}

#content blockquote ul {
padding: 0px;
	margin: 0px;
	list-style: square;
}

#content h2 {
font-size:1.8em;
font-weight:bold;
color:#FF9900;
padding:0px;
font-family: Eurostyle,"Arial Black",impact,serif;
}

#content h3 {
font-size:1.5em;font-weight:normal;margin:6px 0 6px 0;padding:0;
}

#content img {
padding:1px;display:inline;
}

#content a {
font-weight:bold;
color:#FFFFFF;
}
#content a {
font-weight:bold;
}
#content a.sh[hreflang]:after,#onblog p {font-size:0.9em}
a[hreflang]:after {color:#4b4b4B;content:" [" attr(hreflang) "]";font-size:0.8em;font-weight:normal;vertical-align:text-top}
#content ul {
list-style-type: square;
}

#footer a {
color:#808080;
background-color:inherit;
text-decoration:none;
}

#footer a:hover {
text-decoration:underline;
}

#accessibilite {
margin-left:56px;
margin-top:-3px;
padding-top:0px;
}

/* totem */

#_avmenu {
clear:left;
float:left;
width:180px;
margin:0px 0px 10px 0px;
padding:0px;
}

#avmenu {
clear:left;
float:left;
width:180px;
margin:0px 0px 10px 10px;
padding:0px;
margin-top:-80px;
}

/* menu gauche */

#menu {
position: relative; 
z-index:100;
left:0px;
width: 100%; /* correction pour Opera */
}

#menu dl, dt, dd, ul, li {
margin: 0;
padding: 0;
list-style-type: none;
}

#menu dl {
float: left;
}

#menu dt {
cursor: pointer;
margin: 2px 0px 0px 0px;
height: 20px;
line-height: 20px;
text-align: left;
font-weight: bold;
color:#ffffff;
background:#0069AA 2px center no-repeat ;
padding:2px;
padding-left:16px;
}


#menu dt:hover {
background:#FF9900 2px center no-repeat;
}

#menu dd {
/*display: none;*/
border: 0px solid #FF9900;
}

#menu li {
text-align: left;
border: 0 none;
}

#menu li a, #menu dt a {
color: #0069AA;
text-decoration: none;
display: block;
border: 0 none;
height: 100%;
margin-left:10px;
padding-left:10px;
background:#ffffff 2px center no-repeat ;
}

#menu li a:hover, #menu li a:focus, #menu dt a:hover, #menu dt a:focus {
background:#FF9900 2px center no-repeat;
}

/* menu horizontal */

#nav {
	padding: 0px;
	margin: 0px;
	list-style: none;
}

#nav ul {
	padding: 0px;
	margin: 0px;
	list-style: none;
}

#nav ul li {
	float:left; 
	margin-right:1px; 
	position:relative; 
	margin-left:0px;
	}
	
#nav ul li ul {
	display: none;
	}

#nav a {
	display: block;
	text-decoration:none; 
	color:#ffffff; 
	width:178px; 
	height:20px; 
	text-align:left; 
	font-weight:bold;
	background:#0069AA 2px center no-repeat ;
	border:1px solid #0069AA; 
	font-size:11px;
	text-align:center;
	line-height:19px;
}

#nav a.mon-espace {
	display: block;
	text-decoration:none; 
	color:#ffffff; 
	width:178px; 
	height:20px; 
	text-align:left; 
	font-weight:bold;
	background:#ff9900 2px center no-repeat ;
	border:1px solid #ff9900; 
	font-size:11px;
	text-align:center;
	line-height:19px;
}

#nav a:hover {
	background:#ff9900 2px center no-repeat ;
	border:1px solid #ff9900; 
	line-height:19px;
}

#nav li {
	float: left;
	width: 182px;
}

#nav li ul {
	position: absolute;
	width: 182px;
	left: -999em;
border:1px solid #ff9900;
}

#nav li ul li a{
	background:#FFFFFF 2px center no-repeat;
	border:0px solid #ffffff;
	color:#0069AA;
	text-align:left;
	padding-left:14px;
	width: 166px;
	font-weight:normal;
}

#nav li ul li a:hover{
	background:#FF9900 2px center no-repeat;
	font-weight:normal;
	border:0px solid #ffffff;
}

#nav li:hover ul {
	left: auto;
}

#nav li:hover ul, #nav li.sfhover ul {
	left: auto;
}



#connexion {
	float:right; 
	}

#connexion a {
	display: block;
	text-decoration:none; 
	color:#0069AA; 
	width:130px; 
	height:21px; 
	text-align:left; 
	font-weight:bold;
	background:transparent 2px center no-repeat ;
	border:0px solid #0069AA; 
	font-size:11px;
	text-align:right;
	line-height:19px;
}

#connexion a:hover {
	color:#ffffff;
	background:transparent 2px center no-repeat ;
	border:0px solid #ff9900; 
	line-height:19px;
}
