/* i really want this to be global */
#yddWrapper {
  transform: scale(1.2);
  transform-origin: left center;
}
#yddWrapper #yddContainer {  
  font-family: Arial, "PingFang SC", "Microsof Yahei", sans-serif;
}

#yddWrapper #yddTopBorderlr > span:nth-child(3) {
  font-size: 16px!important;
}

#yddWrapper #yddMiddle > .ydd-trans-wrapper > div.ydd-tabs {
  display: none;
}

#yddWrapper #yddMiddle .ydd-trans-wrapper:not(:first-child) {
  border-top: 1px solid #93dab8;
}
