.highlight
{color: #000C91 !important; font-weight: bold;}

/**special threads under 'Other Community Discussions'**/
.ubuntu_hlite a {color: #0A1D46 !important;}

/**# viewing, # of threads, # of posts**/
.forumhome_stats {color: #000 !important; opacity: 0.6 !important;}

/**Ubuntu Forums**/
.ubuntu_roundnavbar_content td, .ubuntu_roundnavbar_content .navbar, .ubuntu_roundnavbar *, .ubuntu_roundnavbar b, div.ubuntu_roundnavbar_content {background: #8094AF !important;}
.ubuntu_roundnavbar * {border-left: 1px solid #8094AF !important; border-right: 1px solid #8094AF !important}

/**login box**/
div.ubunu_loginboxfg, .ubunu_loginbox * {background: #6C7B8E !important;}
.ubunu_loginbox * {border-left: 1px solid #6C7B8E !important; border-right: 1px solid #6C7B8E  !important;}
b.ubunu_loginbox {background: #8094AF !important;}

/**News & Announcements, 3rd Party Project Forums...**/
div.ubuntu_announcefg, .ubuntu_announce * {background: #8094AF !important;}
.ubuntu_announce * {border-left: 1px solid #8094AF !important; border-right: 1px solid #8094AF !important;}

/**Announcement**/
div.ubuntu_announcement_content, .ubuntu_announcement_content div {background: #D7E7F6 !important;}

.tcat {border-bottom: 1px solid #48596F !important;}

/**Absolute Beginner Talk**/
div.ubuntu_forumhomelayoutfg, .ubuntu_forumhomelayoutfg .smallfont, .ubuntu_forumhomelayout * {background: #8094AF !important;}
.ubuntu_forumhomelayout * {border-left: 1px solid #8094AF !important; border-right: 1px solid #8094AF !important;}

/**thread listing divider**/
.sticky, .threadbit {border-bottom:1px solid #5B6677 !important;}

/**Community Cafe:**/
.ubuntu_navbits_info *, div .ubuntu_navbits_info_content, .ubuntu_navbits_info_content td {background: #8094AF !important;}
.ubuntu_navbits_info * {border-left: #8094AF !important; border-right: #8094AF !important;}

/**latest post box(es)**/
div.ubuntu_lastpostfg, .ubuntu_lastpostfg .smallfont div, .ubuntu_lastpost * {background: #627285 !important;}
.ubuntu_lastpost * {border-left: 1px solid #627285 !important; border-right: 1px solid #627285 !important;}

/**Unanswered Posts button**/
.ubuntu_unapbuttonfg {background: #6C7B8E !important;}
.ubuntu_unapbutton * {background: #6C7B8E !important;}
.ubuntu_unapbutton * {border-left: #6C7B8E !important; border-right: #6C7B8E !important;}

/**Post Reply**/
div.ubuntu_buttons_content, .ubuntu_buttons * {background: #8094AF !important;}
.ubuntu_buttons * {border-left: #8094AF !important; border-right: #8094AF !important;}

/**Mark Forums Read**/
div.uf_normal_roundfg, .uf_normal_round * {background: #8094AF !important;}
.uf_normal_round * {border-left: 1px solid #8094AF !important; border-right: 1px solid #8094AF !important;}

