css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(4chan.org) {

body
{ 
	background: none top left no-repeat #e7e6e6 !important;
	color: #000000 !important;
	font: 70% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
}

th[bgcolor="#e04000"]
{ 
	border: 1px solid !important;
	background-color: #bebebe !important;
	font: 90% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif!important;
	font-weight: 700 !important;
	padding: 0px 0px 0px 0px !important;
}

td { color: #000000 !important; }
td[align="right"] { font-size: 70% !important; }
img[align="left"] { border: 1px solid !important; }

.filesize { font: 100% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important; }
.filetitle, .replytitle { color: #cd7474 !important; font: 110% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important; }

a { font-weight: 700 !important; color: #000000 !important; }
a:visited { font-weight: 700 !important; }
a:hover { color: #b2b2b2 !important; }
.quotelink { color: #000000 !important; }
.linkification-ext { color: #000000 !important; }

.oldpost { color: #26569f !important; }
textarea, .inputtext { font: 80% Calibri !important; vertical-align: top !important; }
[value="Submit"] { height: 20px !important; vertical-align: top !important; font: 80% Calibri !important; }
[value="Previous"] { width: 60px !important; font: 80% Calibri !important; }


.postblock  {
	vertical-align: top !important;
	background-color: #fc87c6 !important;
	border-color: #000000 !important;
}

.postblock:not([align="center"])  {
	padding-top: 0.1850em !important;
	-moz-border-radius: 10px 0 0 10px !important;
	vertical-align: top !important;
	text-align: right !important;
	background-color: #f4f4f4 !important;
	border-color: #000000 !important;
}

blockquote {
	font-size: 100% !important;
}

.forwardlink { color: green !important; }
textarea.inputtext { height: 150px !important; }
.commentpostername a.linkmail[href="mailto:sage"]:after { content: " (sage)"; color: #ff0000 !important }
.quotejs:after { content: " " !important } 
.deletebuttons .inputtext { width: 100px !important; -moz-border-radius: 10px !important; }
.pointer:after { content: " " !important; }

.reply, .replyhl, .pages {
	border: 1px solid !important;
	border-color: #d8d8d8 !important;
	-moz-border-radius: 10px 10px 10px 10px !important;
	color: #000000 !important;
	font-size: 70% !important;
}

.reply {
	background-color: #f9f9fa !important;
	padding-right: 10px !important;	
}

.replyhl, #backwash_tipcell {
	background: url("data:image/gif;base64,R0lGODlhBQAFAKIAAAAAAP////b29tra2tnZ2f///wAAAAAAACH5BAEAAAUALAAAAAAFAAUAAAMISLKrLI7FNRIAOw==") !important;
	padding-right: 10px !important;	
}

.pages {
	background-color: #f8f8f8 !important;
	padding-right: 5px !important;	
	padding-bottom: 0px !important;	
}

.reply .reply, .replyhl .replyhl, .pages .pages {
	font-size: 100% !important;
}

.postername {
	font: 120% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
	font-weight: 700 !important;
}

.commentpostername, .linkmail:not([href="mailto:sage"]), .postername {
	color: #6b9b61 !important;
}

.linkmail[href="mailto:sage"] {
	color: #ff0000 !important;
}

.commentpostername {
	font: 120% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
	font-weight: 700 !important;
}

.linkmail {
	font: 100% "Calibri", "Verdana", "Tahoma", Georgia, Sans-Serif !important;
	font-weight: 700 !important;
}

[style^="color: rgb(128, 0, 128)"] { color: #3414ae !important; }

.postertrip {
	color: #bf0000 !important;
	font-size: 90% !important;
}

.unkfunc { color: #7e7e7e !important; }
.logo font { color: #000000 !important; }
hr { border-color: #ffffff !important; }
b:not([style="color: red;"]) { color: #000000 !important; }
b[style="color: red;"] { color: #bf0000 !important; }
span[style="color: rgb(128, 0, 128);"] { color: #753b75 !important; }
.tn_thread {
	background-color: #e5e5e5 !important;
	border-color: #000000 !important;
}
navtop, #navtop, navbot, #navbot { color: #818181 !important; font-size: 90% !important; }
div#tw.reply { font-size: 100% !important; }

/* Ads */

#ad{ display:none !important; }
img[src*="http://content.4chan.org/dontblockthis"]{ display:none !important; }
center+hr{ display:none !important; }
hr[size="1"][width="90%"]{ display:none !important; }
[src*="static.4chan.org/support"] { display: none !important; border: 0 !important; }

/* Various */


#thread_filter {
	font-size: 100% !important;
	padding-left: 10px !important;
	border: 0 !important;
}

#twi_table_div { border: 0 !important; }

.boxbar {
	background-color: #e7e6e6 !important;
	color: #000000 !important;
}

#ft li {
	background-color: #e7e6e6 !important; 
	border-color: #000000 !important;
}

hr { color: #000000 !important; }

html { background: #e7e6e6 !important; }

.boxcontent, .box-outer {
	background-color: #ffffff !important;
	color: #000000 !important;
	border-color: #000000 !important;
}

#recent-images li { background-color: #4b4b4b !important; }

.content h3 {
	background-color: #e7e6e6 !important;
	color: #000000 !important;
	border-color: #000000 !important;
}

