css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torrentvault.org") {
body
{
	font-size: 8pt !important;
	color: #444 !important;
	min-width: 950px !important;
	background-color: #DDD !important;
}
a {
	color: #666 !important;
}

a:hover {
	color: #777 !important;
}
h2
{
	color: #444 !important;
}
#content {
	background-color: #EEE !important;
}
#menu {
	background-color: #DDD !important;
}

#menu ul li {
	background-color: #CCC !important;
}

#menu ul li a {
	color: #444 !important;
}
#menu ul li a:hover {
	background-color: #EEE !important;
	color: #444 !important;
}
#index #nav_index, #torrents #nav_torrents, #requests #nav_requests, #forums #nav_forums, #top10 #nav_top10, #rules #nav_rules, #staff #nav_staff, #chat #nav_irc, #wiki #nav_wiki, #collage #nav_collages {
	background-color: #EEE !important;
}
#userinfo {
	background: #CCC !important;
	color: #444 !important;
}
#userinfo a {
	color: #444 !important;
}
#userinfo span.stat {
	color: #222 !important;
}
#searchbars {
	background-color: #DDD !important;
}
#searchbars input {
	background-color: #EEE !important;
	color: #555 !important;
}
#alerts {
	background-color: #EEE !important;
}
#alerts a {
	color: #555 !important;
}
.alertbar.blend {
	background-color: #DDD !important;
	border: 1px solid #BBB !important;
}
#footer
{
	color: #444 !important;
}
.alertbar {
	background-color: #d7d7d7 !important;
	color: #444 !important;
}

.head {
	border-bottom: 1px solid #BBB !important;
	background: #CCC !important;
	color: #444 !important;
}
.box {
	background-color: #DDD !important;
	border: 1px solid #BBB !important;
}

.tags a {
	color: #666 !important;
}
table {
	border: 1px solid #BBB !important;
}

tr {
	border: 1px solid #BBB !important;
	background-color: #DDD !important;
}

tr.rowa {
	background-color: #EEE !important;
}
tr.rowb {
	background-color: #DDD !important;
}
tr.rowc {
	background-color: #FFF !important;
}

td {
	border: 1px solid #BBB !important;
}

.colhead {
	background: #CCC !important;
	color: #444 !important;
}

.colhead_dark {
	background: #CCC !important;
	color: #444 !important;
}
.colhead_dark a {
	color: #333 !important;
}
.torrent_table tr.group, tr.torrent {
	background-color: #DDD !important;
}
table.forum_post td {
	border: 1px solid #BBB !important;
}
table.forum_unread {
	border: 2px solid #FF7C00 !important;
}
tr.unreadpm {
	background-color: #ECC !important;
}
form tr {
	background-color: #DDD !important;
}
fieldset
{
	background-color: #DDD !important;
	border: 1px solid #BBB !important;
}
fieldset table tr.switch_row_1
{
	background-color: #EEE !important;
}

fieldset table tr.switch_row_2
{
	background-color: #DDD !important;
}
#logo
{
	background: url('http://i48.tinypic.com/5dpb8g.png') no-repeat center center !important;
}
span[style = "color: gold;"] {
color: #F28B05 !important;
}
}
