css:
/*      ..:: Free Radical ::...       */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("erodov.com")

{

/* Body */

table[width="97%"], table[width="1000"], .page {
	max-width: 97% !important;
	width: 95% !important;
	min-width: 90% !important;
}

.menupadding {
	padding:0 !important;
	width:11% !important;
	}

.menupadding > a {
	width:99% !important;
	line-height:20px;
	}
	
TABLE[width="60%"][align="right"] {
	width:100% !important;
	}
	
#threadslist > TBODY > TR:first-child > TD.tcat {
	height: 44px !important;
	padding: 0 16px !important;
	}

.tborder {
	background:none !important;
	}

TD[width="310px"], .tcorner, .bcorner, .tcat_left, .tcat_right, img[src*="corner"], img[src*="menubg"], img[src*="crn_"], img[src*="nav_"], img[title="Register Now"], td[width="11"], TABLE[bgcolor="white"][align="center"]
	{display: none !important;}

	
/* Background Colors */

html > body , body, html > body > a + table, .tborder, hr {
background:#424d64 !important;
background-color:#424d64 !important;
color:#424d64 !important;
	}
	
p, .postdata, .message, TD[style*="border"] {
border-color: #424d64 !important;
	}

.imagebutton, .wysiwyg, .postdata {
background-color:#f4fcff !important;
	}
	
.ebul_cbinsmenu, .ebul_cbinsmenu ul, .ebul_cbinsmenu tr, .ebul_cbinsmenu td {
background-color: #424d64 !important;
border: #424d64 !important;
	}

/* Gradients */

.blank, .tbody, .page, .message, tr[valign="top"], tr[valign="bottom"], .wysiwyg, .vBulletin_editor, .alt1, .alt2, .panel, .panelsurround, .vbmenu_option, textarea, #cybcb_messagearea, .fieldset, .wysiwyg, #vB_Editor_001_iframe, #vB_Editor_QR_iframe, BR, iframe, BODY[class="wysiwyg"] {
	background: -webkit-gradient(linear,left top,right bottom,color-stop(0, #f4fcff),color-stop(0.5, #ecfbfe),color-stop(1, #bbd9f0)) !important;
	}
	
.alt1[width="200"], table[width="200"] > tbody > tr > .alt2, .alt1Active, td[valign="bottom"] {
	background: -webkit-gradient(linear,right bottom,left top,color-stop(0, #f4fcff),color-stop(0.5, #ecfbfe),color-stop(1, #bbd9f0)) !important;
	}

TD[bgcolor="#ffdc84"] {
	background: -webkit-gradient(linear,left top,left bottom,color-stop(0, #f4fcff),color-stop(0.5, #ecfbfe),color-stop(1, #bbd9f0)) !important;
	}
 
table[width="60%"] > tbody > tr > td, a.tab, a.tab2, .tcat, .thead, .tfoot, .vbmenu_control, .menu, .menu_r > a, .menu_ar, .menupadding > a, .page_top, .nav, #collapseobj_attachkey > TR:first-child > TD, .footer, CENTER > TABLE > TBODY > TR > TD:first-child + TD + TD {
	background: -webkit-gradient(linear,left top,left bottom,color-stop(0, #acb3c3),color-stop(0.17, #5d687d),color-stop(0.33, #424d64),color-stop(1, #576684)) !important;
	}

.tab:hover, .tab2:hover, .vbmenu_control:hover, .menu:hover, .menu_r > a:hover, .menu_ar:hover, .menupadding > a:hover, .nav:hover, #collapseobj_attachkey > TR:first-child > TD:hover, .footer:hover, .tcat:hover, .thead:hover, .tfoot:hover {
	background: -webkit-gradient(linear,left top,left bottom,color-stop(0, #a9d0ff),color-stop(0.17, #5890e9),color-stop(0.33, #3e78d7),color-stop(1, #529fff)) !important;
	}

IMG[src*="ebbtcbinsmenu"] {
opacity:0.5 !important;
	}

IMG[src*="ebbtcbinsmenu"]:hover {
opacity:1 !important;
	}

/* Font colors */

div, .alt1, .alt2, .alt1 > .smallfont, .alt1Active > div > .smallfont, td.smallfont, span.smallfont, dl.smallfont, .wysiwyg > .smallfont {
color:black !important;
	}

a:link, body, a:visited, body_avisited, .vbmenu_hilite, .vbmenu_hilite a, .shade, .time, legend, .vbmenu_option > strong {
color:#333366 !important;
	}
	
SPAN[style="color: blue;"] {
	color:#3a6cdd !important;
	font-weight: bold !important;
	}
	
SPAN[style="color: gold;"] {
	color:#ff9900 !important;
	font-weight: bold !important;
	}

SPAN[style="color: rgb(0, 74, 0);"] {
	font-weight: bold !important;
	}

/*
a:hover {
font-style:italic !important;
	}
*/

.nav a:hover, .menupadding a:hover, .ebul_cbinsmenu a:hover  {
text-decoration:underline !important;
	}

.nav, .nav a, .nav a:hover, .nav:hover a, .menupadding, .menupadding a, .menupadding:hover a, .menupadding a:hover, a.tab, a.tab2, .tcat, .tcat a, .tcat a:link, .tcat_alink, .tcat > div, .thead, .thead a, .thead a:link, .headerlinks a, .tfoot, .tfoot a,.thead_alink, .vbmenu_control, .vbmenu_control a, .tcat > .smallfont, form > div > .smallfont, .tfoot > .smallfont, body > .smallfont, .normal, .bubplastic.horizontal ul li a, .footer > .smallfont > strong > a, .ebul_cbinsmenu a {
color:white !important;
	}


TD[valign="bottom"] {
background:#424d64 !important;
background-color:#424d64 !important;
color:#424d64 !important;
	}

/* Default font */

html, body, span, div, td, br, input, font, button, select, option, legend, li, dl, a, p {
	font-family: Segoe UI !important;
	}

}
